  1286â†’async function buildSequence() {
  1287â†’    if (isOperationInProgress) return;
  1288â†’
  1289â†’    isOperationInProgress = true;
  1290â†’    setStatus('Building sequence...');
  1291â†’
  1292â†’    try {
  1293â†’        const settings = getSettings();
  1294â†’
  1295â†’        // Ensure we have sequence info
  1296â†’        if (!currentSequenceInfo) {
  1297â†’            currentSequenceInfo = await jsx.call('getActiveSequence');
  1298â†’        }
  1299â†’
  1300â†’        // Create cut list from selected silences
  1301â†’        const selectedSilences = previewSilences.filter((_, i) => selectedSilenceIndices.has(i));
  1302â†’
  1303â†’        // Calculate total duration from sequence info or last silence
  1304â†’        let duration = 0;
  1305â†’        if (currentSequenceInfo && currentSequenceInfo.duration) {
  1306â†’            duration = currentSequenceInfo.duration;
  1307â†’        } else if (previewSilences.length > 0) {
  1308â†’            const lastSilence = previewSilences[previewSilences.length - 1];
  1309â†’            duration = lastSilence.end + 10; // Add buffer
  1310â†’        }
  1311â†’
  1312â†’        // Call backend to generate cut list
  1313â†’        const cutListResponse = await fetchWithTimeout(
  1314â†’            `${getBackendUrl()}/cut-list`,
  1315â†’            {
  1316â†’                method: 'POST',
  1317â†’                headers: getAuthHeaders(),
  1318â†’                body: JSON.stringify({
  1319â†’                    sourceName: currentSequenceInfo?.name || 'Untitled Sequence',
  1320â†’                    sourcePath: currentSequenceInfo?.treePath || currentSequenceInfo?.name || 'Untitled',
  1321â†’                    duration: duration,
  1322â†’                    silences: selectedSilences,
  1323â†’                    takes: previewTakes,
  1324â†’                    enableTakes: settings.enableTakesDetection,
  1325â†’                    jCutOffset: settings.enableJCut ? settings.jcutLeadIn : 0,
  1326â†’                    lCutOffset: settings.enableJCut ? settings.jcutLeadOut : 0
  1327â†’                })
  1328â†’            }
  1329â†’        );
  1330â†’
  1331â†’        if (!cutListResponse.ok) {
  1332â†’            throw new Error(await parseErrorResponse(cutListResponse));
  1333â†’        }
  1334â†’
  1335â†’        const cutList = await cutListResponse.json();
  1336â†’
  1337â†’        // Build sequence via JSX
  1338â†’        const result = await jsx.call('buildSequenceFromCutList', JSON.stringify(cutList));
  1339â†’
  1340â†’        if (result && result.success) {
  1341â†’            setStatus(`Built sequence: ${result.sequenceName}`);
  1342â†’            hidePreview();
  1343â†’        } else {
  1344â†’            throw new Error(result?.error || 'Build failed');
  1345â†’        }
  1346â†’    } catch (error) {
  1347â†’        setStatus('Build failed: ' + error.message, true);
  1348â†’    } finally {
  1349â†’        isOperationInProgress = false;
  1350â†’    }
  1351â†’}
