  1614â†’/**
  1615â†’ * Look up license key by email address
  1616â†’ */
  1617â†’async function lookupLicense() {
  1618â†’    const lookupEmailInput = document.getElementById('lookupEmailInput');
  1619â†’    const licenseKeyInput = document.getElementById('licenseKeyInput');
  1620â†’
  1621â†’    if (!lookupEmailInput) return;
  1622â†’
  1623â†’    const email = lookupEmailInput.value.trim().toLowerCase();
  1624â†’    if (!email) {
  1625â†’        setStatus('Please enter your email address', true);
  1626â†’        return;
  1627â†’    }
  1628â†’
  1629â†’    // Basic email validation
  1630â†’    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  1631â†’    if (!emailRegex.test(email)) {
  1632â†’        setStatus('Please enter a valid email address', true);
  1633â†’        return;
  1634â†’    }
  1635â†’
  1636â†’    const lookupBtn = document.getElementById('lookupLicenseBtn');
  1637â†’    if (lookupBtn) {
  1638â†’        lookupBtn.disabled = true;
  1639â†’        lookupBtn.textContent = 'Looking up...';
  1640â†’    }
  1641â†’
  1642â†’    try {
  1643â†’        const response = await fetchWithTimeout(
  1644â†’            `${getBackendUrl()}/license/lookup`,
  1645â†’            {
  1646â†’                method: 'POST',
  1647â†’                headers: { 'Content-Type': 'application/json' },
  1648â†’                body: JSON.stringify({ email })
  1649â†’            },
  1650â†’            30000
  1651â†’        );
  1652â†’
  1653â†’        const result = await response.json();
  1654â†’
  1655â†’        // Note: For security, the backend does NOT return the license key directly.
  1656â†’        // It sends the key via email to prevent enumeration attacks.
  1657â†’        if (result.success) {
  1658â†’            setStatus(result.message || 'If a license exists, it has been sent to your email.');
  1659â†’        } else {
  1660â†’            setStatus(result.error || 'Lookup failed. Please try again.', true);
  1661â†’        }
  1662â†’    } catch (error) {
  1663â†’        setStatus('Lookup failed: ' + error.message, true);
  1664â†’    } finally {
  1665â†’        if (lookupBtn) {
  1666â†’            lookupBtn.disabled = false;
  1667â†’            lookupBtn.textContent = 'Lookup';
  1668â†’        }
  1669â†’    }
  1670â†’}
