    height: 100%;
    background: rgba(0, 0, 0, 0.7);
    display: flex;
    align-items: center;
    justify-content: center;
    z-index: 1000;
}

.music-align-modal.hidden,
.music-timeline-modal.hidden {
    display: none;
}

.music-align-content,
.music-timeline-content {
    background: var(--bg-card);
    border-radius: 8px;
    width: 90%;
    max-width: 280px;
    padding: 16px;
    border: 1px solid var(--glass-border);
}

.music-align-header,
.music-timeline-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 12px;
}

.music-align-header span,
.music-timeline-header span {
    font-size: 12px;
    font-weight: 600;
    color: #e1e1e1;
}

.music-align-info,
.music-timeline-info {
    font-size: 10px;
    color: var(--text-muted);
    margin-bottom: 12px;
    padding: 8px;
    background: var(--bg-panel);
    border-radius: 4px;
