    justify-content: space-between;
    margin-bottom: 4px;
}

.music-align-info .info-label {
    color: #666;
}

.music-align-info .info-value {
    color: #e1e1e1;
}

.music-align-actions,
.music-timeline-actions {
    margin-top: 12px;
}

.music-align-actions .btn,
.music-timeline-actions .btn {
    width: 100%;
}

/* Hidden utility */
.hidden {
    display: none !important;
}

/* Collapsed utility */
.collapsed {
    display: none !important;
}

/* Scrollbar */
