    margin-bottom: 4px;
    background: rgba(0, 0, 0, 0.2);
    word-break: break-all;
}

.debug-log-entry:contains('âœ“') {
    color: #4ade80;
}

.debug-log-entry:contains('âœ—') {
    color: #ef4444;
}

.debug-log-entry:contains('Test') {
    color: var(--accent-primary);
    font-weight: 500;
}

/* Debug button in header */
#debugBtn {
