}

/* ============================================================================
   ACCESSIBILITY IMPROVEMENTS (WCAG 2.1 Level AA)
   ============================================================================ */

/* Screen reader only utility class */
.sr-only {
    position: absolute;
    width: 1px;
    height: 1px;
    padding: 0;
    margin: -1px;
    overflow: hidden;
    clip: rect(0, 0, 0, 0);
    white-space: nowrap;
    border: 0;
}

/* Focus visibility for keyboard navigation */
*:focus-visible {
    outline: 2px solid var(--accent-primary);
    outline-offset: 2px;
}

/* Remove focus outline for mouse users, keep for keyboard */
*:focus:not(:focus-visible) {
    outline: none;
}

/* Touch target size improvements - minimum 44x44px touch area */
.icon-btn {
    min-width: 44px;
    min-height: 44px;
    /* Visual size can remain smaller, but touch area is 44px */
}

.close-btn {
    min-width: 44px;
    min-height: 44px;
}

/* Checkbox touch targets */
.checkbox-label {
    min-height: 44px;
    padding: 8px 4px;
}

.checkbox-label input[type="checkbox"] {
    width: 20px;
    height: 20px;
    /* Touch area expanded via label */
}

/* Button minimum heights */
.btn {
    min-height: 44px;
    padding: 10px 16px;
}

.btn-small {
    min-height: 36px;
    padding: 8px 12px;
}

/* Section toggle touch targets */
.section-toggle {
    min-height: 44px;
    padding: 12px;
}

/* Slider touch targets - increase thumb size */
input[type="range"] {
    height: 44px;
}

input[type="range"]::-webkit-slider-thumb {
    width: 24px;
    height: 24px;
}

/* Select elements */
select {
    min-height: 36px;
    padding: 8px 12px;
}

/* Text inputs */
input[type="text"],
input[type="email"],
input[type="number"],
textarea {
    min-height: 36px;
    padding: 8px 12px;
}

/* Improved color contrast for muted text */
.text-muted,
[class*="muted"] {
    color: #a0a0a0; /* Increased from #888888 for better contrast */
}

/* High contrast mode support */
@media (prefers-contrast: high) {
    :root {
        --text-muted: #c0c0c0;
        --glass-border: rgba(255, 255, 255, 0.3);
    }
}

/* Reduced motion support */
@media (prefers-reduced-motion: reduce) {
    *,
    *::before,
    *::after {
        animation-duration: 0.01ms !important;
        animation-iteration-count: 1 !important;
        transition-duration: 0.01ms !important;
    }
}

