/**
 * Check if user is logged in
 */
function isLoggedIn() {
    const settings = getSettings();
    return !!settings.customerId;
}

/**
 * Logout - clear all auth data
 */
function logout() {
    // Clear all auth-related settings
    saveSettings({
        customerId: null,
        accessToken: null,
        refreshToken: null,
        tokenExpiry: null,
        licenseKey: null
    });

    if (typeof clearCreditsCache === 'function') {
