import { NextResponse } from "next/server";
import type { NextRequest } from "next/server";

// Routes that require authentication
const protectedRoutes = ["/dashboard"];

// Routes that should redirect to dashboard if already authenticated
const authRoutes = ["/login"];

export function middleware(request: NextRequest) {
  const { pathname } = request.nextUrl;

  // Check if the current route is protected
  const isProtectedRoute = protectedRoutes.some(
    (route) => pathname === route || pathname.startsWith(`${route}/`)
  );

  // Check if the current route is an auth route (login, register, etc.)
  const isAuthRoute = authRoutes.some(
    (route) => pathname === route || pathname.startsWith(`${route}/`)
  );

  // Check for auth status cookie (non-HttpOnly cookie set by login API)
  // The actual JWT tokens are in HttpOnly cookies and can't be read here
  const authStatusCookie = request.cookies.get("splice_auth_status");
  const isAuthenticated = authStatusCookie?.value === "authenticated";

  // For protected routes, check authentication
  if (isProtectedRoute) {
    // If no auth status cookie exists, redirect to login
    // The actual token validation will happen server-side via /api/auth/me
    if (!isAuthenticated) {
      const loginUrl = new URL("/login", request.url);
      loginUrl.searchParams.set("redirect", pathname);
      return NextResponse.redirect(loginUrl);
    }
  }

  // For auth routes, redirect to dashboard if already authenticated
  if (isAuthRoute && isAuthenticated) {
    const redirectTo = request.nextUrl.searchParams.get("redirect") || "/dashboard";
    return NextResponse.redirect(new URL(redirectTo, request.url));
  }

  return NextResponse.next();
}

export const config = {
  matcher: [
    /*
     * Match all request paths except for:
     * - api routes (API routes handle their own auth)
     * - _next/static (static files)
     * - _next/image (image optimization files)
     * - favicon.ico (favicon file)
     * - public files (images, downloads, etc.)
     * - files with extensions (.pkg, .exe, .ccx, etc.)
     */
    "/((?!api|_next/static|_next/image|favicon.ico|images|downloads|.*\\..*).*)",
  ],
};

