    77→
    78→// ============================================================================
    79→// JSX BRIDGE - Communicate with Premiere Pro
    80→// ============================================================================
    81→
    82→// JSX timeout configuration (FIX: CEP-CRIT-001 - JSX Bridge Timeout Bug)
    83→const JSX_TIMEOUTS = {
    84→    QUICK: 5000,        // 5s  - simple checks (checkSequenceOpen, getVersion)
    85→    DEFAULT: 30000,     // 30s - most operations (getClips, createMarker)
    86→    LONG: 120000,       // 2m  - export operations, large file I/O
    87→    CRITICAL: 300000    // 5m  - complex timeline operations
    88→};
    89→
    90→const jsx = {
    91→    cs: null,
    92→    _initPromise: null,
    93→    _initialized: false,
    94→    _jsxReady: false,
    95→
    96→    /**
    97→     * Initialize the CSInterface with FireCut-style retry logic
    98→     * Returns a promise that resolves when ready (true/false)
    99→     * Does NOT reject - gracefully handles missing CEP environment
   100→     */
   101→    init: function () {
   102→        if (this._initPromise) return this._initPromise;
   103→
   104→        this._initPromise = this._initWithRetry();
   105→        return this._initPromise;
   106→    },
   107→
   108→    /**
   109→     * Explicitly load the host script to ensure functions are available
   110→     * FIX: Solves "EvalScript error" when manifest loading fails
   111→     */
   112→    loadHostScript: async function() {
   113→        if (!this.cs) return false;
   114→        
   115→        try {
   116→            const extensionPath = this.cs.getSystemPath('extension');
   117→            // Check if we got a valid path
   118→            if (!extensionPath) return false;
   119→
   120→            const scriptPath = extensionPath + '/jsx/hostScript.jsx';
   121→            // Ensure forward slashes for ExtendScript (Windows compatibility)
   122→            const sanitizedPath = scriptPath.replace(/\\/g, '/');
   123→            
   124→            debugLog('[JSX] Explicitly loading host script from:', sanitizedPath);
   125→            
   126→            return new Promise((resolve) => {
   127→                this.cs.evalScript('$.evalFile("' + sanitizedPath + '")', (result) => {
   128→                    debugLog('[JSX] Host script load result:', result);
   129→                    resolve(true);
   130→                });
   131→            });
   132→        } catch (e) {
   133→            console.error('[JSX] Failed to load host script:', e);
   134→            return false;
   135→        }
   136→    },
   137→
   138→    /**
   139→     * FireCut-style retry logic for CSInterface initialization
   140→     * Retries up to 5 times with exponential backoff
   141→     */
   142→    _initWithRetry: async function () {
   143→        const maxRetries = 5;
   144→        const baseDelay = 200; // Start with 200ms, will double each retry
   145→
   146→        for (let attempt = 1; attempt <= maxRetries; attempt++) {
   147→            const success = await this._tryInit(attempt);
   148→            if (success) {
   149→                // Explicitly load host script first to prevent EvalScript errors
   150→                await this.loadHostScript();
   151→
   152→                // After CSInterface init, verify JSX is ready
   153→                const jsxReady = await this._verifyJSXReady();
   154→                if (jsxReady) {
   155→                    this._jsxReady = true;
   156→                    debugLog(`[JSX] Full initialization complete on attempt ${attempt}`);
   157→                    return true;
   158→                } else {
   159→                    debugLog(`[JSX] CSInterface ready but JSX not responding, attempt ${attempt}/${maxRetries}`);
   160→                }
   161→            }
   162→
   163→            if (attempt < maxRetries) {
   164→                const delay = baseDelay * Math.pow(2, attempt - 1);
   165→                debugLog(`[JSX] Init attempt ${attempt} failed, retrying in ${delay}ms...`);
   166→                await this._delay(delay);
   167→            }
   168→        }
   169→
   170→        console.warn('[JSX] Initialization failed after all retries - panel may have limited functionality');
   171→        return this._initialized;
   172→    },
   173→
   174→    /**
   175→     * Single initialization attempt
   176→     */
   177→    _tryInit: function (attempt) {
   178→        return new Promise((resolve) => {
   179→            try {
   180→                if (typeof CSInterface !== 'undefined') {
   181→                    this.cs = new CSInterface();
   182→                    // Verify we're in a real CEP environment by checking for __adobe_cep__
   183→                    if (window.__adobe_cep__) {
   184→                        this._initialized = true;
   185→                        debugLog(`[JSX] CSInterface initialized successfully (CEP environment detected) - attempt ${attempt}`);
   186→                        resolve(true);
   187→                    } else {
   188→                        // CSInterface exists but no CEP runtime - browser/dev mode
   189→                        this._initialized = true;
   190→                        debugLog(`[JSX] CSInterface initialized (development/browser mode) - attempt ${attempt}`);
   191→                        resolve(true);
   192→                    }
   193→                } else {
   194→                    debugLog(`[JSX] CSInterface not yet available - attempt ${attempt}`);
   195→                    resolve(false);
   196→                }
   197→            } catch (err) {
   198→                console.error(`[JSX] Init attempt ${attempt} error:`, err);
   199→                resolve(false);
   200→            }
   201→        });
   202→    },
   203→
   204→    /**
   205→     * FireCut-style JSX readiness verification
   206→     * Tests that hostScript.jsx is actually loaded and responding
   207→     */
   208→    _verifyJSXReady: async function () {
   209→        if (!this.cs) return false;
   210→
   211→        return new Promise((resolve) => {
   212→            const timeout = setTimeout(() => {
   213→                debugLog('[JSX] JSX readiness check timed out (increased to 10s)');
   214→                resolve(false);
   215→            }, 10000);
   216→
   217→            try {
   218→                // Simple test that hostScript.jsx is loaded and responding
   219→                this.cs.evalScript('(function() { return typeof getActiveSequence === "function" ? "ready" : "not_ready"; })()', (result) => {
   220→                    clearTimeout(timeout);
   221→                    if (result === 'ready') {
   222→                        debugLog('[JSX] JSX bridge verified ready - hostScript.jsx loaded');
   223→                        resolve(true);
   224→                    } else {
   225→                        debugLog('[JSX] JSX functions not yet available:', result);
   226→                        resolve(false);
   227→                    }
   228→                });
   229→            } catch (err) {
   230→                clearTimeout(timeout);
   231→                debugLog('[JSX] JSX readiness check error:', err);
   232→                resolve(false);
   233→            }
   234→        });
   235→    },
   236→
   237→    /**
   238→     * Helper for async delay
   239→     */
   240→    _delay: function (ms) {
   241→        return new Promise(resolve => setTimeout(resolve, ms));
   242→    },
   243→
   244→    /**
   245→     * Ensure JSX is initialized before making calls
   246→     */
   247→    ensureInitialized: async function () {
   248→        if (!this._initialized) {
   249→            await this.init();
   250→        }
   251→        if (!this.cs) {
   252→            throw new Error('CSInterface not initialized. Make sure you are running in CEP environment.');
   253→        }
   254→    },
   255→
   256→    /**
   257→     * Execute ExtendScript in Premiere Pro with timeout protection
   258→     * FIX: CEP-CRIT-001 - JSX Bridge Timeout Bug
   259→     * @param {string} script - ExtendScript to execute
   260→     * @param {number} timeout - Timeout in ms (default: JSX_TIMEOUTS.DEFAULT)
   261→     * @returns {Promise<any>} - Parsed result
   262→     */
   263→    evalScript: async function (script, timeout = JSX_TIMEOUTS.DEFAULT) {
   264→        await this.ensureInitialized();
   265→
   266→        return new Promise((resolve, reject) => {
   267→            let completed = false;
   268→
   269→            // Timeout handler - rejects if ExtendScript hangs
   270→            const timeoutId = setTimeout(() => {
   271→                if (!completed) {
   272→                    completed = true;
   273→                    const scriptPreview = script.length > 100 ? script.substring(0, 100) + '...' : script;
   274→                    const error = new Error(`ExtendScript timeout after ${timeout}ms: ${scriptPreview}`);
   275→                    error.code = 'JSX_TIMEOUT';
   276→                    console.error('[JSX] Timeout:', scriptPreview);
   277→                    reject(error);
   278→                }
   279→            }, timeout);
   280→
   281→            this.cs.evalScript(script, (result) => {
   282→                // Ignore late callbacks after timeout
   283→                if (completed) {
   284→                    console.warn('[JSX] Late evalScript result received after timeout - ignoring');
   285→                    return;
   286→                }
   287→
   288→                completed = true;
   289→                clearTimeout(timeoutId);
   290→
   291→                if (result === 'undefined' || result === undefined) {
   292→                    resolve(null);
   293→                    return;
   294→                }
   295→
   296→                // Check for EvalScript error or ExtendScript runtime errors
   297→                if (typeof result === 'string' && (
   298→                    result.indexOf('EvalScript error') !== -1 ||
   299→                    result.indexOf('Error:') !== -1 ||
   300→                    result.indexOf('TypeError:') !== -1 ||
   301→                    result.indexOf('ReferenceError:') !== -1
   302→                )) {
   303→                    reject(new Error(result));
   304→                    return;
   305→                }
   306→
   307→                // Try to parse JSON
   308→                try {
   309→                    const parsed = JSON.parse(result);
   310→                    if (parsed.error) {
   311→                        reject(new Error(parsed.error));
   312→                    } else {
   313→                        resolve(parsed);
   314→                    }
   315→                } catch (e) {
   316→                    // Return as-is if not JSON
   317→                    resolve(result);
   318→                }
   319→            });
   320→        });
   321→    },
   322→
   323→    /**
   324→     * Call a JSX function with arguments
   325→     * @param {string} funcName - Function name
   326→     * @param {...any} args - Arguments to pass (last arg can be {timeout: number})
   327→     * @returns {Promise<any>}
   328→     */
   329→    call: async function (funcName, ...args) {
   330→        await this.ensureInitialized();
   331→
   332→        // Check if last argument is an options object with timeout
   333→        let timeout = JSX_TIMEOUTS.DEFAULT;
   334→        let funcArgs = args;
   335→
   336→        if (args.length > 0 && typeof args[args.length - 1] === 'object' && args[args.length - 1] !== null) {
   337→            const lastArg = args[args.length - 1];
   338→            if ('_jsxTimeout' in lastArg) {
   339→                timeout = lastArg._jsxTimeout;
   340→                funcArgs = args.slice(0, -1);
   341→            }
   342→        }
   343→
   344→        const escapedArgs = funcArgs.map(arg => {
   345→            if (arg === null || arg === undefined) return 'null';
   346→            if (typeof arg === 'string') {
   347→                // Escape backslashes first, then other special chars
   348→                return `'${arg
   349→                    .replace(/\\/g, '\\\\')
   350→                    .replace(/'/g, "\\'")
   351→                    .replace(/\r/g, '\\r')
   352→                    .replace(/\n/g, '\\n')
   353→                    .replace(/\t/g, '\\t')}'`;
   354→            }
   355→            if (typeof arg === 'object') {
   356→                return `'${JSON.stringify(arg)
   357→                    .replace(/\\/g, '\\\\')
   358→                    .replace(/'/g, "\\'")}'`;
   359→            }
   360→            return String(arg);
   361→        });
   362→
   363→        const script = `${funcName}(${escapedArgs.join(', ')})`;
   364→        return this.evalScript(script, timeout);
   365→    },
   366→
   367→    /**
   368→     * Call a JSX function with explicit timeout
   369→     * Convenience wrapper for operations that need non-default timeouts
   370→     * @param {string} funcName - Function name
   371→     * @param {Array} args - Arguments array
   372→     * @param {number} timeout - Timeout in ms
   373→     * @returns {Promise<any>}
   374→     */
   375→    callWithTimeout: async function (funcName, args = [], timeout = JSX_TIMEOUTS.DEFAULT) {
   376→        return this.call(funcName, ...args, { _jsxTimeout: timeout });
   377→    }
   378→};
   379→
   380→// ============================================================================
   381→// FETCH UTILITIES
   382→// ============================================================================
   383→
   384→/**
   385→ * Fetch with timeout support
   386→ * @param {string} url - URL to fetch
   387→ * @param {object} options - Fetch options
   388→ * @param {number} timeout - Timeout in ms
   389→ */
   390→async function fetchWithTimeout(url, options = {}, timeout = SPLICE_CONFIG.FETCH_TIMEOUT) {
   391→    // DIAGNOSTIC: Log fetch request details (debug mode only)
   392→    debugFetch('=== FETCH REQUEST START ===');
   393→    debugFetch('URL:', url);
   394→    debugFetch('Method:', options?.method || 'GET');
   395→    debugFetch('Headers:', options?.headers);
   396→    debugFetch('Timeout:', timeout);
   397→    debugFetch('Is CEP environment:', typeof CSInterface !== 'undefined');
   398→    debugFetch('Is localhost URL:', url.includes('localhost') || url.includes('127.0.0.1'));
   399→    debugFetch('=================================');
   400→
   401→    const controller = new AbortController();
   402→    const timeoutId = setTimeout(() => controller.abort(), timeout);
   403→
   404→    try {
   405→        const response = await fetch(url, {
   406→            ...options,
   407→            signal: controller.signal
   408→        });
   409→        clearTimeout(timeoutId);
   410→
   411→        // DIAGNOSTIC: Log successful response (debug mode only)
   412→        debugFetch('=== FETCH RESPONSE SUCCESS ===');
   413→        debugFetch('Status:', response.status);
   414→        debugFetch('OK:', response.ok);
   415→        debugFetch('=================================');
   416→
   417→        return response;
   418→    } catch (error) {
   419→        clearTimeout(timeoutId);
   420→
   421→        // DIAGNOSTIC: Log raw fetch error (debug mode only)
   422→        debugFetch('=== FETCH ERROR CAUGHT ===');
   423→        debugFetch('Error name:', error?.name);
   424→        debugFetch('Error message:', error?.message);
   425→        debugFetch('Error type:', typeof error);
   426→        debugFetch('Error constructor:', error?.constructor?.name);
   427→        debugFetch('Is AbortError:', error.name === 'AbortError');
   428→        debugFetch('Is TypeError:', error.name === 'TypeError');
   429→        debugFetch('Is NetworkError:', error.name === 'TypeError' && error.message?.includes('Failed to fetch'));
   430→        debugFetch('URL that failed:', url);
   431→        debugFetch('Full error object:', JSON.stringify(error, Object.getOwnPropertyNames(error)));
   432→        debugFetch('=================================');
   433→
   434→        if (error.name === 'AbortError') {
   435→            throw new Error(`Request timeout after ${timeout}ms`);
   436→        }
   437→        throw error;
   438→    }
   439→}
   440→
   441→/**
   442→ * Parse error response from backend
   443→ * @param {Response} response - Fetch response
   444→ */
   445→async function parseErrorResponse(response) {
   446→    try {
   447→        const data = await response.json();
   448→        return data.error || data.message || `HTTP ${response.status}`;
   449→    } catch {
   450→        return `HTTP ${response.status}: ${response.statusText}`;
   451→    }
   452→}
