   454→/**
   455→ * Get auth headers for API requests
   456→ * Uses JWT Bearer token if available, with legacy fallback
   457→ */
   458→function getAuthHeaders() {
   459→    const headers = { 'Content-Type': 'application/json' };
   460→    const settings = getSettings();
