   478→/**
   479→ * Check if the access token is expired or about to expire
   480→ * @returns {boolean} True if token needs refresh
   481→ */
   482→function isTokenExpired() {
   483→    const settings = getSettings();
   484→    if (!settings.tokenExpiry) return true;
   485→
   486→    // Refresh if less than 5 minutes remaining
   487→    const bufferMs = 5 * 60 * 1000;
   488→    return Date.now() >= (settings.tokenExpiry - bufferMs);
   489→}
   490→
   491→/**
   492→ * Refresh the access token using the refresh token
   493→ * @returns {Promise<boolean>} True if refresh succeeded
   494→ */
   495→async function refreshAccessToken() {
   496→    const settings = getSettings();
   497→
   498→    if (!settings.refreshToken) {
   499→        debugLog('[Auth] No refresh token available');
   500→        return false;
   501→    }
   502→
   503→    try {
   504→        debugLog('[Auth] Refreshing access token...');
   505→
   506→        const response = await fetchWithTimeout(`${getBackendUrl()}/auth/refresh`, {
   507→            method: 'POST',
   508→            headers: { 'Content-Type': 'application/json' },
   509→            body: JSON.stringify({ refreshToken: settings.refreshToken })
   510→        }, 30000);
   511→
   512→        if (!response.ok) {
   513→            const errorMsg = await parseErrorResponse(response);
   514→            console.error('[Auth] Token refresh failed:', errorMsg);
   515→
   516→            // If refresh token is invalid, clear auth and require re-login
   517→            if (response.status === 401) {
   518→                clearAuthTokens();
   519→            }
   520→            return false;
   521→        }
   522→
   523→        const data = await response.json();
   524→
   525→        // Save new access token
   526→        const expiresInMs = (data.expiresIn || 86400) * 1000; // Default 24h
   527→        saveSettings({
   528→            accessToken: data.token,
   529→            tokenExpiry: Date.now() + expiresInMs
   530→        });
   531→
   532→        debugLog('[Auth] Access token refreshed successfully');
   533→        return true;
   534→    } catch (err) {
   535→        console.error('[Auth] Token refresh error:', err);
   536→        return false;
   537→    }
   538→}
   539→
   540→/**
   541→ * Clear all auth tokens (call on logout or auth failure)
   542→ */
   543→function clearAuthTokens() {
   544→    saveSettings({
   545→        accessToken: null,
   546→        refreshToken: null,
   547→        tokenExpiry: null
   548→    });
   549→    debugLog('[Auth] Auth tokens cleared');
   550→}
   551→
   552→/**
   553→ * Ensure we have a valid access token, refreshing if needed
   554→ * @returns {Promise<boolean>} True if we have a valid token
   555→ */
   556→async function ensureValidToken() {
   557→    const settings = getSettings();
   558→
   559→    // No token at all - need to login
   560→    if (!settings.accessToken) {
   561→        return false;
   562→    }
   563→
   564→    // Token not expired - good to go
   565→    if (!isTokenExpired()) {
   566→        return true;
   567→    }
   568→
   569→    // Try to refresh
   570→    return await refreshAccessToken();
   571→}
