  1022→
  1023→let isOnlineState = true;
  1024→let offlineCheckInterval = null;
  1025→
  1026→/**
  1027→ * Check if online
  1028→ */
  1029→function isOnline() {
  1030→    return isOnlineState && navigator.onLine;
  1031→}
  1032→
  1033→/**
  1034→ * Initialize offline detection
  1035→ */
  1036→function initOfflineDetection() {
  1037→    // Browser events
  1038→    window.addEventListener('online', () => {
  1039→        isOnlineState = true;
  1040→        updateOfflineUI(true);
  1041→    });
  1042→
  1043→    window.addEventListener('offline', () => {
  1044→        isOnlineState = false;
  1045→        updateOfflineUI(false);
  1046→    });
  1047→
  1048→    // Periodic check
  1049→    offlineCheckInterval = setInterval(async () => {
  1050→        try {
  1051→            const response = await fetchWithTimeout(
  1052→                `${getBackendUrl()}/health`,
  1053→                { method: 'GET' },
  1054→                5000
  1055→            );
  1056→            isOnlineState = response.ok;
  1057→        } catch {
  1058→            isOnlineState = false;
  1059→        }
  1060→        updateOfflineUI(isOnlineState);
  1061→    }, 30000);
  1062→}
  1063→
  1064→/**
  1065→ * Update UI based on online state
