  1128→/**
  1129→ * Format time in MM:SS or HH:MM:SS
  1130→ */
  1131→function formatTime(seconds) {
  1132→    if (isNaN(seconds) || seconds < 0) return '0:00';
  1133→
  1134→    const hours = Math.floor(seconds / 3600);
  1135→    const minutes = Math.floor((seconds % 3600) / 60);
  1136→    const secs = Math.floor(seconds % 60);
  1137→
  1138→    if (hours > 0) {
  1139→        return `${hours}:${minutes.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  1140→    }
  1141→    return `${minutes}:${secs.toString().padStart(2, '0')}`;
  1142→}
