  1155→/**
  1156→ * Debounce function calls
  1157→ */
  1158→function debounce(func, wait) {
  1159→    let timeout;
  1160→    return function executedFunction(...args) {
  1161→        const later = () => {
  1162→            clearTimeout(timeout);
  1163→            func(...args);
  1164→        };
  1165→        clearTimeout(timeout);
  1166→        timeout = setTimeout(later, wait);
  1167→    };
  1168→}
