  1840→
  1841→    return log.map((entry, i) => {
  1842→        return [
  1843→            `--- Error ${i + 1} ---`,
  1844→            `Time: ${entry.timestamp}`,
  1845→            `Module: ${entry.module}`,
  1846→            `Operation: ${entry.operation}`,
  1847→            `Message: ${entry.message}`,
  1848→            entry.code ? `Code: ${entry.code}` : null,
  1849→            entry.stack ? `Stack: ${entry.stack}` : null,
  1850→            `CEP: ${entry.context?.isCEP ? 'Yes' : 'No'}`,
  1851→            `Online: ${entry.context?.isOnline ? 'Yes' : 'No'}`,
  1852→            ''
  1853→        ].filter(Boolean).join('\n');
  1854→    }).join('\n');
  1855→}
  1856→
  1857→// ============================================================================
  1858→// GLOBAL ERROR HANDLERS (FIX: CEP-ERR-008)
  1859→// Catch unhandled errors and promise rejections
  1860→// ============================================================================
  1861→
  1862→/**
  1863→ * Global error handler for uncaught exceptions
  1864→ */
  1865→window.addEventListener('error', (event) => {
  1866→    logError('Global', 'Uncaught Exception', event.error || event.message, {
  1867→        filename: event.filename,
  1868→        lineno: event.lineno,
