    17→    NONE: 0,
    18→    VIOLET: 1,
    19→    IRIS: 2,
    20→    CARIBBEAN: 3,
    21→    LAVENDER: 4,
    22→    CERULEAN: 5,
    23→    FOREST: 6,
    24→    ROSE: 7,
    25→    MANGO: 8,
    26→    PURPLE: 9,
    27→    BLUE: 10,
    28→    TEAL: 11,
    29→    MAGENTA: 12,
    30→    TAN: 13,
    31→    GREEN: 14,
    32→    BROWN: 15,
    33→    YELLOW: 16
