    54→    "Position": ["Position", "Posicion", "Posicao"],
    55→    "Anchor Point": ["Anchor Point", "Point d'ancrage", "Ankerpunkt"],
    56→    "Rotation": ["Rotation", "Rotacion", "Rotacao"],
    57→    "Opacity": ["Opacity", "Opacite", "Deckkraft", "Opacidad"]
