    79→ */
    80→function getActualFrameRate() {
    81→    var secsPerFrame = app.project.activeSequence.getSettings().videoFrameRate.seconds;
    82→    return 1 / secsPerFrame;
    83→}
    84→
    85→function quantise_time(time_seconds) {
    86→    var secsPerFrame = app.project.activeSequence.getSettings().videoFrameRate.seconds;
    87→    return secsPerFrame * Math.round(time_seconds / secsPerFrame);
    88→}
    89→
    90→function frames_to_time(number_of_frames) {
    91→    var secsPerFrame = app.project.activeSequence.getSettings().videoFrameRate.seconds;
    92→    return number_of_frames * secsPerFrame;
    93→}
    94→
    95→function frames_to_ticks(number_of_frames) {
    96→    var secsPerFrame = app.project.activeSequence.getSettings().videoFrameRate.seconds;
    97→    return number_of_frames * secsPerFrame * TICKS_PER_SECOND;
    98→}
    99→
   100→function seconds_to_ticks(seconds) {
   101→    return seconds * TICKS_PER_SECOND;
   102→}
   103→
   104→function ticks_to_seconds(ticks) {
   105→    return ticks / TICKS_PER_SECOND;
   106→}
