   211→// ============================================================================
   212→// UNIFIED WORKFLOW (Silence Detection + Takes Detection)
   213→// ============================================================================
   214→function initUnifiedWorkflow() {
   215→  if (!ui.goBtn) return;
   216→
   217→  ui.goBtn.addEventListener('click', async () => {
   218→    // Prevent double-click
   219→    if (isOperationInProgress) {
   220→      setStatus('Operation in progress...');
   221→      return;
   222→    }
   223→
   224→    isOperationInProgress = true;
   225→    ui.goBtn.disabled = true;
   226→
   227→    try {
   228→      showProgress('Preparing audio...');
   229→
   230→      // Step 1: Export audio
   231→      const exportSuccess = await exportAudioInternal();
   232→      if (!exportSuccess) {
   233→        throw new Error('Audio export failed');
   234→      }
   235→
   236→      // Step 2: Get settings
   237→      const sensitivity = parseInt(ui.sensitivitySlider?.value || 50);
   238→      const useIsolated = ui.sourceIsolated?.checked ?? false;
   239→      const detectTakes = ui.enableTakesDetection?.checked ?? true;
   240→
   241→      // Step 3: Vocal isolation if needed
   242→      let audioPath = WAV_PATH;
   243→      if (useIsolated) {
   244→        // Check isolation access before proceeding
   245→        const credits = getCurrentCredits();
   246→        if (!credits || !credits.hasIsolationAccess) {
   247→          throw new Error('Vocal isolation requires Pro or Team tier. Upgrade to access this feature.');
   248→        }
   249→
   250→        // Show overage warning if applicable
   251→        const isolationMins = credits.isolationHoursRemaining * 60;
   252→        if (isolationMins <= 0) {
   253→          const overageRate = credits.isolationOverageRate || 0.08;
   254→          showProgress(`Isolating vocals (overage: $${overageRate}/min)...`);
   255→        } else {
   256→          showProgress(`Isolating vocals (${isolationMins.toFixed(0)} min remaining)...`);
   257→        }
   258→
   259→        const isolateResult = await isolateVocals();
   260→        if (isolateResult.success) {
   261→          audioPath = isolateResult.outputPath;
   262→          // Update credits display after isolation
   263→          await refreshCredits();
   264→        } else {
   265→          throw new Error(isolateResult.error || 'Vocal isolation failed');
   266→        }
   267→      }
   268→
   269→      // Step 4: Run detection (parallel if takes enabled)
   270→      const params = mapSensitivity(sensitivity);
   271→      let silences = [];
   272→      let takes = [];
   273→
   274→      if (detectTakes) {
   275→        // Parallel detection for performance
   276→        showProgress('Detecting silences and analyzing takes...');
   277→        const [silenceResult, transcriptResult] = await Promise.all([
   278→          detectSilences(audioPath, params),
   279→          transcribeAudio()
   280→        ]);
   281→
   282→        silences = silenceResult;
   283→
   284→        // Extract takes from transcript result
   285→        if (transcriptResult.success && transcriptResult.takes) {
   286→          if (transcriptResult.takes.takes) {
   287→            takes = transcriptResult.takes.takes;
   288→          } else if (Array.isArray(transcriptResult.takes)) {
   289→            takes = transcriptResult.takes;
   290→          }
   291→        }
   292→
   293→        // Store transcript for reference
   294→        window.currentTranscript = transcriptResult.transcript;
   295→      } else {
   296→        // Just detect silences
   297→        showProgress('Detecting silences...');
   298→        silences = await detectSilences(audioPath, params);
   299→      }
   300→
   301→      if (silences.length === 0) {
   302→        showEmptyState();
   303→        setStatus('No silences detected. Try increasing sensitivity.');
   304→        return;
   305→      }
   306→
   307→      // Step 5: Filter silences by takes (protect speech)
   308→      const filtered = filterSilencesByTakes(silences, takes);
   309→
   310→      // Step 6: Add timeline markers (clear old ones first)
   311→      showProgress('Adding timeline markers...');
   312→      await clearSpliceMarkers();
   313→      const markerResult = await addTimelineMarkers(filtered.safe, filtered.protected, takes);
   314→
   315→      // Step 7: Show combined preview
   316→      showCombinedPreview(filtered.safe, filtered.protected, takes);
   317→
   318→      const markerInfo = markerResult.added > 0 ? ` (${markerResult.added} markers added)` : '';
   319→      const statusMsg = takes.length > 0
   320→        ? `Found ${silences.length} silences, ${filtered.protected.length} protected by ${takes.length} takes${markerInfo}`
   321→        : `Found ${silences.length} silence(s) - review and apply${markerInfo}`;
   322→      setStatus(statusMsg);
   323→
   324→    } catch (err) {
   325→      showEmptyState();
   326→      setStatus('Error: ' + err.message);
   327→      console.error('[SPLICE] Unified workflow error:', err);
   328→    } finally {
   329→      isOperationInProgress = false;
   330→      ui.goBtn.disabled = false;
   331→    }
   332→  });
   333→}
