   553→/**
   554→ * Remove all SPLICE markers from the timeline.
   555→ * Call this before adding new markers to avoid duplicates.
   556→ */
   557→async function clearSpliceMarkers() {
   558→  try {
   559→    const context = await getActiveSequence();
   560→    if (!context) return 0;
   561→
   562→    const { sequence, project } = context;
   563→    const markers = await ppro.Markers.getMarkers(sequence);
   564→    if (!markers) return 0;
   565→
   566→    // Get all markers
   567→    const allMarkers = await markers.getMarkers();
   568→    if (!allMarkers || allMarkers.length === 0) return 0;
   569→
   570→    // Filter SPLICE markers
   571→    const spliceMarkers = [];
   572→    for (const marker of allMarkers) {
   573→      const name = await marker.getName();
   574→      if (name && name.startsWith('SPLICE:')) {
   575→        spliceMarkers.push(marker);
   576→      }
   577→    }
   578→
   579→    if (spliceMarkers.length === 0) return 0;
   580→
   581→    // Remove all SPLICE markers in a single transaction
   582→    await project.lockedAccess(async () => {
   583→      await project.executeTransaction((compoundAction) => {
   584→        for (const marker of spliceMarkers) {
   585→          const removeAction = markers.createRemoveMarkerAction(marker);
   586→          compoundAction.addAction(removeAction);
   587→        }
   588→      }, 'SPLICE: Clear Markers');
   589→    });
   590→
   591→    console.log(`[SPLICE] Cleared ${spliceMarkers.length} previous markers`);
   592→    return spliceMarkers.length;
   593→
   594→  } catch (err) {
   595→    console.error('[SPLICE] Error clearing markers:', err);
   596→    return 0;
   597→  }
   598→}
