  1214→/**
  1215→ * Request a cut list from the backend and build the sequence.
  1216→ * Full v3.5 workflow.
  1217→ */
  1218→async function buildSequenceWithCutList() {
  1219→  if (!window.spliceBuilder) {
  1220→    throw new Error('Builder not initialized');
  1221→  }
  1222→
  1223→  // Ensure BACKEND_URL is available
  1224→  const apiUrl = (typeof BACKEND_URL !== 'undefined') ? BACKEND_URL : 'http://localhost:3000';
  1225→
  1226→  if (typeof showProgress === 'function') showProgress('Getting source clip info...');
  1227→
  1228→  const app = require('premierepro');
  1229→  const project = await app.Project.getActiveProject();
  1230→  if (!project) throw new Error("No active project.");
  1231→
  1232→  const rootItem = await project.getRootItem();
  1233→  const sourceProjectItem = await findFirstMediaItem(rootItem);
  1234→
  1235→  if (!sourceProjectItem) {
  1236→    throw new Error('No media items found in project. Please import a media file first.');
  1237→  }
  1238→
  1239→  // Use properties strictly
  1240→  const sourceName = sourceProjectItem.name;
  1241→  const sourcePath = sourceProjectItem.treePath;
  1242→
  1243→  const durationObj = await sourceProjectItem.getDuration();
  1244→  const duration = durationObj ? durationObj.seconds : 0;
  1245→
  1246→  // Retrieve Globals
  1247→  const silences = window.splicePreviewState?.getSelectedSilences() || (typeof safeSilences !== 'undefined' ? safeSilences : []);
  1248→  const takes = (typeof previewTakes !== 'undefined' ? previewTakes : []);
  1249→
  1250→  if (silences.length === 0 && (!takes || takes.length === 0)) {
  1251→     throw new Error('No detection data found. Run analysis first.');
  1252→  }
  1253→
  1254→  if (typeof showProgress === 'function') showProgress('Generating cut list...');
  1255→
  1256→  const response = await fetch(`${apiUrl}/cut-list`, {
  1257→    method: 'POST',
  1258→    headers: getAuthHeaders(),
  1259→    body: JSON.stringify({
  1260→      sourceName,
  1261→      sourcePath,
  1262→      duration,
  1263→      silences,
  1264→      takes
  1265→    })
  1266→  });
  1267→
  1268→  if (!response.ok) {
  1269→    const err = await response.json();
  1270→    throw new Error(err.error || 'Failed to generate cut list');
  1271→  }
  1272→
  1273→  const data = await response.json();
  1274→  const cutList = data.cutList;
  1275→
  1276→  if (typeof showProgress === 'function') showProgress('Building sequence...');
  1277→
  1278→  const result = await window.spliceBuilder.buildSequenceFromCutList(cutList, sourceProjectItem);
  1279→
  1280→  if (!result.success) {
  1281→    throw new Error(result.error || 'Failed to build sequence');
  1282→  }
  1283→
  1284→  return {
  1285→    success: true,
  1286→    clipsAdded: result.clipsAdded,
  1287→    metadata: cutList.metadata
  1288→  };
  1289→}
  1290→
  1291→// Expose functions globally for UI and testing
  1292→if (typeof window !== 'undefined') {
  1293→    window.buildSequenceV35 = buildSequenceV35;
  1294→    window.buildSequenceWithCutList = buildSequenceWithCutList;
  1295→    window.findFirstMediaItem = findFirstMediaItem;
  1296→}
