  1301→async function seekToTime(seconds) {
  1302→  // Validate input
  1303→  if (typeof seconds !== 'number' || isNaN(seconds) || !isFinite(seconds)) {
  1304→    setStatus('Invalid seek time');
  1305→    return;
  1306→  }
  1307→
  1308→  if (seconds < 0) {
  1309→    seconds = 0;
  1310→  }
  1311→
  1312→  try {
  1313→    const context = await getActiveSequence();
  1314→    if (!context) {
  1315→      setStatus('No sequence available');
  1316→      return;
  1317→    }
  1318→
  1319→    const { sequence } = context;
  1320→
  1321→    // Use TickTime API to convert seconds to the format needed
  1322→    const position = ppro.TickTime.createWithSeconds(seconds);
  1323→
  1324→    await sequence.setPlayerPosition(position);
  1325→    setStatus(`Seeked to ${formatTime(seconds)}`);
  1326→  } catch (err) {
  1327→    console.error('[SPLICE] Seek error:', err);
  1328→    setStatus('Seek failed: ' + (err.message || 'Unknown error'));
  1329→  }
  1330→}
