  1446→let batchQueue = [];
  1447→let currentBatchJobId = null;
  1448→
  1449→/**
  1450→ * Initialize batch processing handlers
  1451→ */
  1452→function initBatchHandlers() {
  1453→  const addToBatchBtn = document.getElementById('addToBatchBtn');
  1454→  const startBatchBtn = document.getElementById('startBatchBtn');
  1455→  const clearBatchBtn = document.getElementById('clearBatchBtn');
  1456→  const batchSection = document.getElementById('batchSection');
  1457→
  1458→  // Show batch section in settings or after first detection
  1459→  if (batchSection) {
  1460→    batchSection.classList.remove('hidden');
  1461→  }
  1462→
  1463→  // Add files to batch queue
  1464→  if (addToBatchBtn) {
  1465→    addToBatchBtn.addEventListener('click', async () => {
  1466→      try {
  1467→        const uxpFs = require('uxp').storage.localFileSystem;
  1468→        const files = await uxpFs.getFileForOpening({
  1469→          allowMultiple: true,
  1470→          types: ['wav', 'mp3', 'mp4', 'mov', 'aac', 'm4a']
  1471→        });
  1472→
  1473→        if (files && files.length > 0) {
  1474→          for (const file of files) {
  1475→            const path = file.nativePath || file.name;
  1476→            if (!batchQueue.includes(path)) {
  1477→              batchQueue.push(path);
  1478→            }
  1479→          }
  1480→          updateBatchQueueDisplay();
  1481→          setStatus(`Added ${files.length} file(s) to batch queue`);
  1482→        }
  1483→      } catch (err) {
  1484→        setStatus('Error adding files: ' + err.message);
  1485→      }
  1486→    });
  1487→  }
  1488→
  1489→  // Start batch processing
  1490→  if (startBatchBtn) {
  1491→    startBatchBtn.addEventListener('click', async () => {
  1492→      if (batchQueue.length === 0) {
  1493→        setStatus('No files in batch queue');
  1494→        return;
  1495→      }
  1496→
  1497→      startBatchBtn.disabled = true;
  1498→      addToBatchBtn.disabled = true;
  1499→
  1500→      try {
  1501→        await runBatchProcessing();
  1502→      } finally {
  1503→        startBatchBtn.disabled = false;
  1504→        addToBatchBtn.disabled = false;
  1505→      }
  1506→    });
  1507→  }
  1508→
  1509→  // Clear batch queue
  1510→  if (clearBatchBtn) {
  1511→    clearBatchBtn.addEventListener('click', () => {
  1512→      batchQueue = [];
  1513→      currentBatchJobId = null;
  1514→      updateBatchQueueDisplay();
  1515→      setStatus('Batch queue cleared');
  1516→    });
  1517→  }
  1518→}
  1519→
  1520→/**
  1521→ * Update batch queue display
  1522→ */
  1523→function updateBatchQueueDisplay() {
  1524→  const batchEmpty = document.getElementById('batchEmpty');
  1525→  const batchList = document.getElementById('batchList');
  1526→  const startBatchBtn = document.getElementById('startBatchBtn');
  1527→
  1528→  if (batchQueue.length === 0) {
  1529→    if (batchEmpty) batchEmpty.style.display = 'block';
  1530→    if (batchList) batchList.innerHTML = '';
  1531→    if (startBatchBtn) startBatchBtn.disabled = true;
  1532→    return;
  1533→  }
  1534→
  1535→  if (batchEmpty) batchEmpty.style.display = 'none';
  1536→  if (startBatchBtn) startBatchBtn.disabled = false;
  1537→
  1538→  if (batchList) {
  1539→    batchList.innerHTML = batchQueue.map((path, i) => {
  1540→      const fileName = path.split('/').pop();
  1541→      return `<div style="display: flex; justify-content: space-between; align-items: center; padding: 4px 6px; background: #2a2a2a; border-radius: 3px; margin-bottom: 2px; font-size: 9px;">
  1542→        <span style="overflow: hidden; text-overflow: ellipsis; white-space: nowrap; max-width: 180px;" title="${path}">${fileName}</span>
  1543→        <button onclick="removeBatchItem(${i})" style="background: transparent; border: none; color: #888; cursor: pointer; font-size: 10px;">x</button>
  1544→      </div>`;
  1545→    }).join('');
  1546→  }
  1547→}
