  1557→/**
  1558→ * Run batch processing
  1559→ */
  1560→async function runBatchProcessing() {
  1561→  const batchProgress = document.getElementById('batchProgress');
  1562→  const batchProgressBar = document.getElementById('batchProgressBar');
  1563→  const batchProgressText = document.getElementById('batchProgressText');
  1564→
