  1618→        headers: getAuthHeaders()
  1619→      });
  1620→
  1621→      if (!response.ok) {
  1622→        throw new Error('Failed to get batch status');
  1623→      }
  1624→
  1625→      const data = await response.json();
  1626→      const job = data.job;
  1627→
  1628→      // Update progress
  1629→      if (progressBar) progressBar.value = job.progress.percentage;
  1630→      if (progressText) progressText.textContent = `${job.progress.completed + job.progress.failed} / ${job.progress.total} files`;
  1631→
  1632→      // Check if complete
