  1634→        if (job.status === 'completed') {
  1635→          setStatus(`Batch complete: ${job.progress.completed} files processed`);
  1636→        } else if (job.status === 'completed_with_errors') {
  1637→          setStatus(`Batch complete with ${job.progress.failed} error(s)`);
  1638→        } else {
  1639→          setStatus('Batch failed');
