function updateMediaFolderDisplay() {
  if (ui.mediaFolderDisplay) {
    const path = getMediaFolderPath();
    if (path) {
      // Truncate long paths for display
      const displayPath = path.length > 40
        ? '...' + path.slice(-37)
        : path;
      ui.mediaFolderDisplay.textContent = displayPath;
