   377â†’/**
   378â†’ * Check if the access token is expired or about to expire
   379â†’ * @returns {boolean} True if token needs refresh
   380â†’ */
   381â†’function isTokenExpired() {
   382â†’    const settings = getSettings();
   383â†’    if (!settings.tokenExpiry) return true;
   384â†’
   385â†’    // Refresh if less than 5 minutes remaining
   386â†’    const bufferMs = 5 * 60 * 1000;
   387â†’    return Date.now() >= (settings.tokenExpiry - bufferMs);
   388â†’}
   389â†’
   390â†’/**
   391â†’ * Refresh the access token using the refresh token
   392â†’ * @returns {Promise<boolean>} True if refresh succeeded
   393â†’ */
   394â†’async function refreshAccessToken() {
   395â†’    const settings = getSettings();
   396â†’
   397â†’    if (!settings.refreshToken) {
   398â†’        debugLog('[Auth] No refresh token available');
   399â†’        return false;
   400â†’    }
   401â†’
   402â†’    try {
   403â†’        debugLog('[Auth] Refreshing access token...');
   404â†’
   405â†’        const response = await fetchWithTimeout(`${getBackendUrl()}/auth/refresh`, {
   406â†’            method: 'POST',
   407â†’            headers: { 'Content-Type': 'application/json' },
   408â†’            body: JSON.stringify({ refreshToken: settings.refreshToken })
   409â†’        }, 30000);
   410â†’
   411â†’        if (!response.ok) {
   412â†’            const errorMsg = await parseErrorResponse(response);
   413â†’            console.error('[Auth] Token refresh failed:', errorMsg);
   414â†’
   415â†’            // If refresh token is invalid, clear auth and require re-login
   416â†’            if (response.status === 401) {
   417â†’                clearAuthTokens();
   418â†’            }
   419â†’            return false;
   420â†’        }
   421â†’
   422â†’        const data = await response.json();
   423â†’
   424â†’        // Save new access token
   425â†’        const expiresInMs = (data.expiresIn || 86400) * 1000; // Default 24h
   426â†’        saveSettings({
   427â†’            accessToken: data.token,
   428â†’            tokenExpiry: Date.now() + expiresInMs
   429â†’        });
   430â†’
   431â†’        debugLog('[Auth] Access token refreshed successfully');
   432â†’        return true;
   433â†’    } catch (err) {
   434â†’        console.error('[Auth] Token refresh error:', err);
   435â†’        return false;
   436â†’    }
   437â†’}
   438â†’
   439â†’/**
   440â†’ * Clear all auth tokens (call on logout or auth failure)
   441â†’ */
   442â†’function clearAuthTokens() {
   443â†’    saveSettings({
   444â†’        accessToken: null,
   445â†’        refreshToken: null,
   446â†’        tokenExpiry: null
   447â†’    });
   448â†’    debugLog('[Auth] Auth tokens cleared');
   449â†’}
   450â†’
   451â†’/**
   452â†’ * Ensure we have a valid access token, refreshing if needed
   453â†’ * @returns {Promise<boolean>} True if we have a valid token
   454â†’ */
   455â†’async function ensureValidToken() {
   456â†’    const settings = getSettings();
   457â†’
   458â†’    // No token at all - need to login
   459â†’    if (!settings.accessToken) {
   460â†’        return false;
   461â†’    }
   462â†’
   463â†’    // Token not expired - good to go
   464â†’    if (!isTokenExpired()) {
   465â†’        return true;
   466â†’    }
   467â†’
   468â†’    // Try to refresh
   469â†’    return await refreshAccessToken();
   470â†’}
