   512â†’// ============================================================================
   513â†’// ENCRYPTION HELPERS FOR SENSITIVE DATA (CEP-CRIT-002 FIX)
   514â†’// Uses AES-GCM encryption via Web Crypto API for proper security
   515â†’// ============================================================================
   516â†’
   517â†’// Encryption constants
   518â†’const SPLICE_ENCRYPTION = {
   519â†’    ALGORITHM: 'AES-GCM',
   520â†’    KEY_LENGTH: 256,
   521â†’    IV_LENGTH: 12,
   522â†’    TAG_LENGTH: 128,
   523â†’    SALT: 'SPLICE_PANEL_SALT_v1', // Static salt for key derivation
   524â†’    ITERATIONS: 100000,
   525â†’    PREFIX: 'ENC:v1:'  // Prefix to identify encrypted values
   526â†’};
   527â†’
   528â†’// Cached encryption key (derived once per session)
   529â†’let _encryptionKey = null;
   530â†’
   531â†’/**
   532â†’ * Get or derive the encryption key from a machine-specific identifier
   533â†’ * Uses PBKDF2 to derive a strong key from available identifiers
   534â†’ * @returns {Promise<CryptoKey>} The derived encryption key
   535â†’ */
   536â†’async function getEncryptionKey() {
   537â†’    if (_encryptionKey) {
   538â†’        return _encryptionKey;
   539â†’    }
   540â†’
   541â†’    try {
   542â†’        // Use a combination of identifiers for key derivation
   543â†’        // In CEP, we use localStorage ID + app version as the key material
   544â†’        let keyMaterial = localStorage.getItem('splice_device_id');
   545â†’        if (!keyMaterial) {
   546â†’            // Generate a random device ID on first use
   547â†’            keyMaterial = crypto.randomUUID ? crypto.randomUUID() :
   548â†’                'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
   549â†’                    const r = Math.random() * 16 | 0;
   550â†’                    const v = c === 'x' ? r : (r & 0x3 | 0x8);
   551â†’                    return v.toString(16);
   552â†’                });
   553â†’            localStorage.setItem('splice_device_id', keyMaterial);
   554â†’        }
   555â†’
   556â†’        const encoder = new TextEncoder();
   557â†’        const keyData = encoder.encode(keyMaterial + SPLICE_ENCRYPTION.SALT);
   558â†’
   559â†’        // Import the key material for PBKDF2
   560â†’        const baseKey = await crypto.subtle.importKey(
   561â†’            'raw',
   562â†’            keyData,
   563â†’            'PBKDF2',
   564â†’            false,
   565â†’            ['deriveKey']
   566â†’        );
   567â†’
   568â†’        // Derive the actual AES key
   569â†’        _encryptionKey = await crypto.subtle.deriveKey(
   570â†’            {
   571â†’                name: 'PBKDF2',
   572â†’                salt: encoder.encode(SPLICE_ENCRYPTION.SALT),
   573â†’                iterations: SPLICE_ENCRYPTION.ITERATIONS,
   574â†’                hash: 'SHA-256'
   575â†’            },
   576â†’            baseKey,
   577â†’            {
   578â†’                name: SPLICE_ENCRYPTION.ALGORITHM,
   579â†’                length: SPLICE_ENCRYPTION.KEY_LENGTH
   580â†’            },
   581â†’            false,
   582â†’            ['encrypt', 'decrypt']
   583â†’        );
   584â†’
   585â†’        return _encryptionKey;
   586â†’    } catch (e) {
   587â†’        debugLog('[Config] Key derivation error:', e);
   588â†’        return null;
   589â†’    }
   590â†’}
   591â†’
   592â†’/**
   593â†’ * Encrypt data for secure localStorage storage using AES-GCM
   594â†’ *
   595â†’ * @param {any} data - Data to encrypt
   596â†’ * @returns {Promise<string|null>} Encrypted string with prefix or null on error
   597â†’ */
   598â†’async function encryptForStorageAsync(data) {
   599â†’    try {
   600â†’        if (data === null || data === undefined) {
   601â†’            return null;
   602â†’        }
   603â†’
   604â†’        const key = await getEncryptionKey();
   605â†’        if (!key) {
   606â†’            // Fallback to legacy encoding if crypto not available
   607â†’            debugLog('[Config] Crypto not available, using legacy encoding');
   608â†’            return legacyEncode(data);
   609â†’        }
   610â†’
   611â†’        const encoder = new TextEncoder();
   612â†’        const jsonStr = JSON.stringify(data);
   613â†’        const plaintext = encoder.encode(jsonStr);
   614â†’
   615â†’        // Generate random IV
   616â†’        const iv = crypto.getRandomValues(new Uint8Array(SPLICE_ENCRYPTION.IV_LENGTH));
   617â†’
   618â†’        // Encrypt
   619â†’        const ciphertext = await crypto.subtle.encrypt(
   620â†’            {
   621â†’                name: SPLICE_ENCRYPTION.ALGORITHM,
   622â†’                iv: iv,
   623â†’                tagLength: SPLICE_ENCRYPTION.TAG_LENGTH
   624â†’            },
   625â†’            key,
   626â†’            plaintext
   627â†’        );
   628â†’
   629â†’        // Combine IV + ciphertext and encode as base64
   630â†’        const combined = new Uint8Array(iv.length + ciphertext.byteLength);
   631â†’        combined.set(iv);
   632â†’        combined.set(new Uint8Array(ciphertext), iv.length);
   633â†’
   634â†’        // Convert to base64 with prefix
   635â†’        const base64 = btoa(String.fromCharCode(...combined));
   636â†’        return SPLICE_ENCRYPTION.PREFIX + base64;
   637â†’    } catch (e) {
   638â†’        debugLog('[Config] Encryption error:', e);
   639â†’        return legacyEncode(data);
   640â†’    }
   641â†’}
   642â†’
   643â†’/**
   644â†’ * Decrypt data from localStorage using AES-GCM
   645â†’ *
   646â†’ * @param {string} data - Encrypted string
   647â†’ * @returns {Promise<any|null>} Decrypted data or null on error
   648â†’ */
   649â†’async function decryptFromStorageAsync(data) {
   650â†’    try {
   651â†’        if (!data || typeof data !== 'string') {
   652â†’            return null;
   653â†’        }
   654â†’
   655â†’        // Check if this is the new encrypted format
   656â†’        if (!data.startsWith(SPLICE_ENCRYPTION.PREFIX)) {
   657â†’            // Try legacy decoding for backward compatibility
   658â†’            return legacyDecode(data);
   659â†’        }
   660â†’
   661â†’        const key = await getEncryptionKey();
   662â†’        if (!key) {
   663â†’            debugLog('[Config] Crypto not available for decryption');
   664â†’            return null;
   665â†’        }
   666â†’
   667â†’        // Remove prefix and decode base64
   668â†’        const base64Data = data.slice(SPLICE_ENCRYPTION.PREFIX.length);
   669â†’        const combined = Uint8Array.from(atob(base64Data), c => c.charCodeAt(0));
   670â†’
   671â†’        // Extract IV and ciphertext
   672â†’        const iv = combined.slice(0, SPLICE_ENCRYPTION.IV_LENGTH);
   673â†’        const ciphertext = combined.slice(SPLICE_ENCRYPTION.IV_LENGTH);
   674â†’
   675â†’        // Decrypt
   676â†’        const plaintext = await crypto.subtle.decrypt(
   677â†’            {
   678â†’                name: SPLICE_ENCRYPTION.ALGORITHM,
   679â†’                iv: iv,
   680â†’                tagLength: SPLICE_ENCRYPTION.TAG_LENGTH
   681â†’            },
   682â†’            key,
   683â†’            ciphertext
   684â†’        );
   685â†’
   686â†’        const decoder = new TextDecoder();
   687â†’        const jsonStr = decoder.decode(plaintext);
   688â†’        return JSON.parse(jsonStr);
   689â†’    } catch (e) {
   690â†’        // Try legacy decoding as fallback
   691â†’        return legacyDecode(data);
   692â†’    }
   693â†’}
   694â†’
   695â†’/**
   696â†’ * Legacy encoding (Base64) for backward compatibility
   697â†’ * @param {any} data - Data to encode
   698â†’ * @returns {string|null} Encoded string
   699â†’ */
   700â†’function legacyEncode(data) {
   701â†’    try {
   702â†’        if (data === null || data === undefined) {
   703â†’            return null;
   704â†’        }
   705â†’        const jsonStr = JSON.stringify(data);
   706â†’        const encoded = encodeURIComponent(jsonStr);
   707â†’        return btoa(encoded);
   708â†’    } catch (e) {
   709â†’        return null;
   710â†’    }
   711â†’}
   712â†’
   713â†’/**
   714â†’ * Legacy decoding (Base64) for backward compatibility
   715â†’ * @param {string} data - Encoded string
   716â†’ * @returns {any|null} Decoded data
   717â†’ */
   718â†’function legacyDecode(data) {
   719â†’    try {
   720â†’        if (!data || typeof data !== 'string') {
   721â†’            return null;
   722â†’        }
   723â†’        // Skip if it has the encryption prefix
   724â†’        if (data.startsWith(SPLICE_ENCRYPTION.PREFIX)) {
   725â†’            return null;
   726â†’        }
   727â†’        const decoded = atob(data);
   728â†’        const jsonStr = decodeURIComponent(decoded);
   729â†’        return JSON.parse(jsonStr);
   730â†’    } catch (e) {
   731â†’        return null;
   732â†’    }
   733â†’}
   734â†’
   735â†’/**
   736â†’ * Synchronous wrapper for encryption (starts async operation)
   737â†’ * For backward compatibility with existing sync code
   738â†’ * @param {any} data - Data to encode
   739â†’ * @returns {string|null} Encoded string (legacy format until async completes)
   740â†’ */
   741â†’function encryptForStorage(data) {
   742â†’    // Return legacy encoding synchronously
   743â†’    // The async version should be preferred for new code
   744â†’    return legacyEncode(data);
   745â†’}
   746â†’
   747â†’/**
   748â†’ * Synchronous wrapper for decryption
   749â†’ * For backward compatibility with existing sync code
   750â†’ * @param {string} data - Encoded string
   751â†’ * @returns {any|null} Decoded data
   752â†’ */
   753â†’function decryptFromStorage(data) {
   754â†’    // Handle new encrypted format by returning null (needs async)
   755â†’    if (data && data.startsWith(SPLICE_ENCRYPTION.PREFIX)) {
   756â†’        debugLog('[Config] Encrypted data requires async decryption');
   757â†’        return null;
   758â†’    }
   759â†’    return legacyDecode(data);
   760â†’}
   761â†’
   762â†’/**
   763â†’ * Check if a value appears to be encrypted
   764â†’ *
   765â†’ * @param {any} value - Value to check
   766â†’ * @returns {boolean} True if value appears to be encrypted
   767â†’ */
   768â†’function isEncryptedValue(value) {
   769â†’    if (!value || typeof value !== 'string') {
   770â†’        return false;
   771â†’    }
   772â†’    // New encryption format
   773â†’    if (value.startsWith(SPLICE_ENCRYPTION.PREFIX)) {
   774â†’        return true;
   775â†’    }
   776â†’    // Unencrypted values typically start with known patterns
   777â†’    if (value.startsWith('cus_') || value.startsWith('SPLICE-')) {
   778â†’        return false;
   779â†’    }
   780â†’    // Check if it's legacy base64 encoded JSON
   781â†’    try {
   782â†’        const decoded = atob(value);
   783â†’        const jsonStr = decodeURIComponent(decoded);
   784â†’        JSON.parse(jsonStr);
   785â†’        return true;
   786â†’    } catch {
   787â†’        return false;
   788â†’    }
   789â†’}
   790â†’
   791â†’/**
   792â†’ * Encrypt sensitive fields in settings object
   793â†’ *
   794â†’ * @param {Object} settings - Settings object with plaintext sensitive fields
   795â†’ * @returns {Object} Settings with encrypted sensitive fields
   796â†’ */
   797â†’function encryptSensitiveFields(settings) {
   798â†’    const encrypted = { ...settings };
   799â†’
   800â†’    for (const field of SENSITIVE_FIELDS) {
   801â†’        if (encrypted[field] !== null && encrypted[field] !== undefined) {
   802â†’            // Only encrypt if not already encrypted
   803â†’            if (!isEncryptedValue(encrypted[field])) {
   804â†’                encrypted[field] = encryptForStorage(encrypted[field]);
   805â†’                debugLog(`[Config] Encrypted field: ${field}`);
   806â†’            }
   807â†’        }
   808â†’    }
   809â†’
   810â†’    return encrypted;
   811â†’}
   812â†’
   813â†’/**
   814â†’ * Decrypt sensitive fields in settings object
   815â†’ *
   816â†’ * @param {Object} settings - Settings object with encrypted sensitive fields
   817â†’ * @returns {Object} Settings with decrypted sensitive fields
   818â†’ */
   819â†’function decryptSensitiveFields(settings) {
   820â†’    const decrypted = { ...settings };
   821â†’
   822â†’    for (const field of SENSITIVE_FIELDS) {
   823â†’        if (decrypted[field] !== null && decrypted[field] !== undefined) {
   824â†’            // Check if it looks encrypted before attempting to decrypt
   825â†’            if (isEncryptedValue(decrypted[field])) {
   826â†’                const decryptedValue = decryptFromStorage(decrypted[field]);
   827â†’                if (decryptedValue !== null) {
   828â†’                    decrypted[field] = decryptedValue;
   829â†’                    debugLog(`[Config] Decrypted field: ${field}`);
   830â†’                }
   831â†’            }
   832â†’            // If not encrypted (legacy data), keep the original value
   833â†’        }
   834â†’    }
   835â†’
   836â†’    return decrypted;
   837â†’}
   838â†’
   839â†’/**
   840â†’ * Internal save function to avoid recursion during migration
   841â†’ * @private
   842â†’ */
   843â†’function _saveSettingsInternal(settings) {
   844â†’    try {
   845â†’        // Encrypt sensitive fields before storing
   846â†’        const toStore = encryptSensitiveFields(settings);
   847â†’
   848â†’        // Add version marker for future migrations
   849â†’        toStore._storageVersion = SETTINGS_STORAGE_VERSION;
   850â†’
   851â†’        localStorage.setItem('spliceSettings', JSON.stringify(toStore));
   852â†’        return true;
   853â†’    } catch (e) {
   854â†’        console.warn('[Config] Error saving settings:', e);
   855â†’        return false;
   856â†’    }
   857â†’}
   858â†’
   859â†’/**
   860â†’ * Get settings from localStorage
   861â†’ * Handles migration from unencrypted to encrypted storage format
   862â†’ */
   863â†’function getSettings() {
   864â†’    try {
   865â†’        const stored = localStorage.getItem('spliceSettings');
   866â†’        if (stored) {
   867â†’            const parsed = JSON.parse(stored);
   868â†’
   869â†’            // Decrypt sensitive fields (handles both encrypted and legacy unencrypted data)
   870â†’            const decrypted = decryptSensitiveFields(parsed);
   871â†’
   872â†’            // Check if migration is needed (no version marker = legacy data)
   873â†’            if (!parsed._storageVersion) {
   874â†’                // Migrate legacy data: save with encryption and version marker
   875â†’                debugLog('[Config] Migrating legacy settings to encrypted format');
   876â†’                const migratedSettings = { ...DEFAULT_SETTINGS, ...decrypted };
   877â†’                // Use internal save to avoid infinite recursion
   878â†’                _saveSettingsInternal(migratedSettings);
   879â†’            }
   880â†’
   881â†’            // Return merged settings without internal fields
   882â†’            const result = { ...DEFAULT_SETTINGS, ...decrypted };
   883â†’            delete result._storageVersion;
   884â†’            return result;
   885â†’        }
   886â†’    } catch (e) {
   887â†’        console.warn('[Config] Error loading settings:', e);
   888â†’    }
   889â†’    return { ...DEFAULT_SETTINGS };
   890â†’}
   891â†’
   892â†’/**
   893â†’ * Save settings to localStorage
   894â†’ * Encrypts sensitive fields before storing
   895â†’ */
   896â†’function saveSettings(settings) {
   897â†’    try {
   898â†’        // Get current settings (decrypted) and merge with new settings
   899â†’        const current = getSettings();
   900â†’        const merged = { ...current, ...settings };
   901â†’
   902â†’        return _saveSettingsInternal(merged);
   903â†’    } catch (e) {
   904â†’        console.warn('[Config] Error saving settings:', e);
   905â†’        return false;
   906â†’    }
   907â†’}
   908â†’
   909â†’/**
   910â†’ * Clear specific setting
   911â†’ */
   912â†’function clearSetting(key) {
   913â†’    const settings = getSettings();
   914â†’    delete settings[key];
   915â†’    _saveSettingsInternal(settings);
   916â†’}
