   918â†’// ============================================================================
   919â†’// OFFLINE DETECTION
   920â†’// ============================================================================
   921â†’
   922â†’let isOnlineState = true;
   923â†’let offlineCheckInterval = null;
   924â†’
   925â†’/**
   926â†’ * Check if online
   927â†’ */
   928â†’function isOnline() {
   929â†’    return isOnlineState && navigator.onLine;
   930â†’}
   931â†’
   932â†’/**
   933â†’ * Initialize offline detection
   934â†’ */
   935â†’function initOfflineDetection() {
   936â†’    // Browser events
   937â†’    window.addEventListener('online', () => {
   938â†’        isOnlineState = true;
   939â†’        updateOfflineUI(true);
   940â†’    });
   941â†’
   942â†’    window.addEventListener('offline', () => {
   943â†’        isOnlineState = false;
   944â†’        updateOfflineUI(false);
   945â†’    });
   946â†’
   947â†’    // Periodic check
   948â†’    offlineCheckInterval = setInterval(async () => {
   949â†’        try {
   950â†’            const response = await fetchWithTimeout(
   951â†’                `${getBackendUrl()}/health`,
   952â†’                { method: 'GET' },
   953â†’                5000
   954â†’            );
   955â†’            isOnlineState = response.ok;
   956â†’        } catch {
   957â†’            isOnlineState = false;
   958â†’        }
   959â†’        updateOfflineUI(isOnlineState);
   960â†’    }, 30000);
   961â†’}
   962â†’
   963â†’/**
   964â†’ * Update UI based on online state
   965â†’ */
   966â†’function updateOfflineUI(online) {
   967â†’    const goBtn = document.getElementById('goBtn');
   968â†’    const status = document.getElementById('status');
   969â†’
   970â†’    if (goBtn) {
   971â†’        goBtn.disabled = !online;
   972â†’    }
   973â†’
   974â†’    if (status) {
