   990â†’/**
   991â†’ * SECURITY: Escape HTML to prevent XSS attacks
   992â†’ * Use this when inserting untrusted content into innerHTML
   993â†’ * @param {string} str - String to escape
   994â†’ * @returns {string} Escaped string safe for innerHTML
   995â†’ */
   996â†’function escapeHtml(str) {
   997â†’    if (str === null || str === undefined) return '';
   998â†’    if (typeof str !== 'string') str = String(str);
   999â†’
  1000â†’    const escapeMap = {
  1001â†’        '&': '&amp;',
  1002â†’        '<': '&lt;',
  1003â†’        '>': '&gt;',
  1004â†’        '"': '&quot;',
  1005â†’        "'": '&#x27;',
  1006â†’        '/': '&#x2F;',
  1007â†’        '`': '&#x60;'
  1008â†’    };
  1009â†’
  1010â†’    return str.replace(/[&<>"'\/`]/g, char => escapeMap[char]);
  1011â†’}
  1012â†’
  1013â†’/**
  1014â†’ * SECURITY: Create safe HTML from template with escaped values
  1015â†’ * @param {TemplateStringsArray} strings - Template strings
  1016â†’ * @param {...any} values - Values to escape
  1017â†’ * @returns {string} HTML with escaped values
  1018â†’ */
  1019â†’function safeHtml(strings, ...values) {
  1020â†’    return strings.reduce((result, str, i) => {
  1021â†’        const value = values[i - 1];
  1022â†’        const escaped = escapeHtml(value);
  1023â†’        return result + escaped + str;
  1024â†’    });
  1025â†’}
  1026â†’
  1027â†’/**
  1028â†’ * Format time in MM:SS or HH:MM:SS
  1029â†’ */
  1030â†’function formatTime(seconds) {
  1031â†’    if (isNaN(seconds) || seconds < 0) return '0:00';
  1032â†’
  1033â†’    const hours = Math.floor(seconds / 3600);
  1034â†’    const minutes = Math.floor((seconds % 3600) / 60);
  1035â†’    const secs = Math.floor(seconds % 60);
  1036â†’
  1037â†’    if (hours > 0) {
  1038â†’        return `${hours}:${minutes.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  1039â†’    }
  1040â†’    return `${minutes}:${secs.toString().padStart(2, '0')}`;
  1041â†’}
