  1054â†’/**
  1055â†’ * Debounce function calls
  1056â†’ */
  1057â†’function debounce(func, wait) {
  1058â†’    let timeout;
  1059â†’    return function executedFunction(...args) {
  1060â†’        const later = () => {
  1061â†’            clearTimeout(timeout);
  1062â†’            func(...args);
  1063â†’        };
  1064â†’        clearTimeout(timeout);
  1065â†’        timeout = setTimeout(later, wait);
  1066â†’    };
  1067â†’}
