  1314â†’/**
  1315â†’ * Validate license key format: SPLICE-XXXX-XXXX-XXXX
  1316â†’ * Character set excludes confusing chars: 0/O, 1/I/L
  1317â†’ */
  1318â†’function isValidLicenseKeyFormat(key) {
  1319â†’    if (!key || typeof key !== 'string') return false;
  1320â†’    const normalized = key.toUpperCase().trim();
  1321â†’    const pattern = /^SPLICE-[ABCDEFGHJKMNPQRSTUVWXYZ23456789]{4}-[ABCDEFGHJKMNPQRSTUVWXYZ23456789]{4}-[ABCDEFGHJKMNPQRSTUVWXYZ23456789]{4}$/;
  1322â†’    return pattern.test(normalized);
  1323â†’}
