  1368â†’            const licenseKey = licenseKeyInput?.value?.trim()?.toUpperCase();
  1369â†’
  1370â†’            if (!licenseKey) {
  1371â†’                showLoginError('Please enter your license key');
  1372â†’                return;
  1373â†’            }
  1374â†’
  1375â†’            if (!isValidLicenseKeyFormat(licenseKey)) {
  1376â†’                showLoginError('Invalid format. Expected: SPLICE-XXXX-XXXX-XXXX');
  1377â†’                return;
  1378â†’            }
  1379â†’
  1380â†’            saveLoginBtn.disabled = true;
  1381â†’            saveLoginBtn.textContent = 'Activating...';
  1382â†’
  1383â†’            try {
  1384â†’                // Use /auth/login endpoint which returns JWT tokens
  1385â†’                const response = await fetchWithTimeout(`${getBackendUrl()}/auth/login`, {
  1386â†’                    method: 'POST',
  1387â†’                    headers: { 'Content-Type': 'application/json' },
  1388â†’                    body: JSON.stringify({ licenseKey: licenseKey })
  1389â†’                }, SPLICE_CONFIG.FETCH_TIMEOUT);
  1390â†’
  1391â†’                const result = await response.json();
  1392â†’
  1393â†’                if (!result.success) {
  1394â†’                    showLoginError(result.error || 'Activation failed');
  1395â†’                    return;
  1396â†’                }
  1397â†’
  1398â†’                // Store JWT tokens and customer info
  1399â†’                const expiresInMs = (result.expiresIn || 86400) * 1000; // Default 24h
  1400â†’                saveSettings({
  1401â†’                    customerId: result.customerId,
  1402â†’                    accessToken: result.token,
  1403â†’                    refreshToken: result.refreshToken,
  1404â†’                    tokenExpiry: Date.now() + expiresInMs,
  1405â†’                    licenseKey: licenseKey // Keep for re-authentication
  1406â†’                });
  1407â†’
  1408â†’                loginModal?.classList.add('hidden');
  1409â†’
  1410â†’                if (typeof refreshCredits === 'function') {
  1411â†’                    await refreshCredits();
  1412â†’                }
  1413â†’
  1414â†’                const tierName = result.tier ? result.tier.charAt(0).toUpperCase() + result.tier.slice(1) : 'Unknown';
  1415â†’                setStatus(`License activated! ${tierName} tier`);
