  1418â†’
  1419â†’                // DIAGNOSTIC LOGGING: Capture exact error details (debug mode only)
  1420â†’                debugLog('=== CEP LICENSE ACTIVATION ERROR DIAGNOSTICS ===');
  1421â†’                debugLog('Error name:', err?.name);
  1422â†’                debugLog('Error message:', err?.message);
  1423â†’                debugLog('Error stack:', err?.stack);
  1424â†’                debugLog('Backend URL:', getBackendUrl());
  1425â†’                debugLog('Fetch timeout:', SPLICE_CONFIG.FETCH_TIMEOUT);
  1426â†’                debugLog('Is CEP environment:', typeof CSInterface !== 'undefined');
  1427â†’                debugLog('User agent:', navigator?.userAgent);
  1428â†’                debugLog('Error constructor:', err?.constructor?.name);
  1429â†’                debugLog('Is TypeError:', err?.name === 'TypeError');
  1430â†’                debugLog('Message contains "Failed to fetch":', err?.message?.includes('Failed to fetch'));
  1431â†’                debugLog('Message contains "fetch":', err?.message?.toLowerCase()?.includes('fetch'));
  1432â†’                debugLog('Message contains "network":', err?.message?.toLowerCase()?.includes('network'));
  1433â†’                debugLog('Message contains "CORS":', err?.message?.toLowerCase()?.includes('cors'));
  1434â†’                debugLog('Message contains "SSL":', err?.message?.toLowerCase()?.includes('ssl'));
  1435â†’                debugLog('Message contains "certificate":', err?.message?.toLowerCase()?.includes('certificate'));
  1436â†’                debugLog('Full error object:', JSON.stringify(err, Object.getOwnPropertyNames(err)));
  1437â†’                debugLog('================================================');
  1438â†’
  1439â†’                // Provide detailed error message based on error type
  1440â†’                let errorMessage = 'Connection error. ';
  1441â†’
  1442â†’                if (err.message === 'Request timed out') {
