 * AI-powered music generation for video projects
 */

// ============================================
// STATE
// ============================================
const musicState = {
  jobs: [],
  selectedJob: null,
  isIdentifying: false,
  isGenerating: false,
  identifiedSong: null,
  audioPlayer: null,
  pollInterval: null,
  currentPollingJobId: null,  // Track which job we're polling to prevent race conditions
  variationsJob: null,
