            headers: getAuthHeaders(),
            body: JSON.stringify({
                transcript,
                template: animatedCaptionsState.selectedTemplate,
                settings: animatedCaptionsState.settings
            })
        }, 120000);

        if (!response.ok) {
            const errorMsg = await parseErrorResponse(response);
            throw new Error(errorMsg);
