let audioFilePath = null;
try {
    // FIX: CEP-CRIT-010 - Main.js Export Timeout
    // Use LONG timeout (120s) for export operations
    const exportResult = await jsx.callWithTimeout(
        'exportSequenceAudioForAnalysis',
        [],
        JSX_TIMEOUTS.LONG
    );
