// ============================================================================
// GET AUDIO PATH (CEP VERSION)
// ============================================================================
async function getAudioPath() {
    // FIX: CEP-CRIT-013 - Multitrack JSX Error Handling
    try {
        // Try to get audio path from active sequence (QUICK timeout)
        const seqInfo = await jsx.callWithTimeout('getActiveSequence', [], JSX_TIMEOUTS.QUICK);
        if (seqInfo && seqInfo.audioPath) {
            return seqInfo.audioPath;
        }

        // Fallback: export sequence audio for analysis (LONG timeout)
        const exportResult = await jsx.callWithTimeout(
            'exportSequenceAudioForAnalysis',
            [],
            JSX_TIMEOUTS.LONG
        );
        if (exportResult && exportResult.path) {
            return exportResult.path;
        }

        // Last resort: get first clip audio path (QUICK timeout)
        const clipPath = await jsx.callWithTimeout('getFirstClipAudioPath', [], JSX_TIMEOUTS.QUICK);
        if (clipPath) {
            return clipPath;
        }

        throw new Error('Could not determine audio path from any source');
    } catch (e) {
        console.error('[SPLICE Multitrack] Failed to get audio path:', e);

        // Provide actionable error message
        if (e.code === 'JSX_TIMEOUT') {
            throw new Error('Premiere Pro is not responding. Try closing other apps or restarting Premiere.');
        } else if (e.message.includes('no sequence')) {
            throw new Error('Please open a sequence in Premiere Pro first.');
        } else {
            throw new Error(`Audio export failed: ${e.message}`);
        }
    }
}

