  1469→                chapters.push({
  1470→                    guid: marker.guid,
  1471→                    name: marker.name,
  1472→                    comments: marker.comments,
  1473→                    start: marker.start.seconds
  1474→                });
  1475→            }
  1476→        }
  1477→
  1478→        return JSON.stringify(chapters);
  1479→    } catch (e) {
  1480→        return JSON.stringify({ error: e.message });
  1481→    }
