  1031→
  1032→    if (status) {
  1033→        if (!online) {
  1034→            status.textContent = 'Offline - Check your connection';
  1035→            status.style.color = '#dc3545';
  1036→        } else {
  1037→            // Reset status when back online
  1038→            status.textContent = 'Ready';
  1039→            status.style.color = '#888';
  1040→        }
  1041→    }
  1042→}
