  1283→    }
  1284→
  1285→    // Apply preset settings
  1286→    const newSettings = {
  1287→        ...preset.settings,
  1288→        activePreset: presetName
  1289→    };
  1290→
  1291→    saveSettings(newSettings);
  1292→    debugLog(`Applied preset: ${preset.name}`);
