/**
 * Slice 7: Apply Takes to Timeline (CEP Port)
 *
 * Timeline manipulation functions via JSX:
 * - Label clips with take information
 * - Delete non-best takes
 *
 * Port of UXP slice7-apply.js for CEP compatibility.
 */

/**
 * Label clips on timeline with take information
 * FIX: CEP-ERR-004 - Added comprehensive error handling for JSX calls
 * @returns {Promise<number>} Number of clips labeled
 */
async function labelTakesOnTimeline() {
    const takes = getCurrentTakes();

    if (!takes || takes.length === 0) {
        throw new Error('No takes to label');
    }

    // Check if sequence is open with timeout protection
    let seqResult;
    try {
        seqResult = await jsx.callWithTimeout('getActiveSequence', [], JSX_TIMEOUTS.QUICK);
        if (!seqResult || seqResult.error) {
            throw new Error('No project or sequence open');
        }
    } catch (e) {
        console.error('[SPLICE Takes] Failed to get active sequence:', e);
        if (e.code === 'JSX_TIMEOUT') {
            throw new Error('Premiere Pro is not responding. Try restarting the application.');
        }
        throw new Error('No project or sequence open: ' + e.message);
    }

    let labeledCount = 0;
    let failCount = 0;

    // Get clips from all video tracks
    let numVideoTracks = 0;
    try {
        numVideoTracks = await jsx.callWithTimeout('getNumTracks', ['video'], JSX_TIMEOUTS.QUICK);
    } catch (e) {
        console.error('[SPLICE Takes] Failed to get video track count:', e);
        throw new Error('Could not read timeline tracks: ' + e.message);
    }

    for (let trackIndex = 0; trackIndex < numVideoTracks; trackIndex++) {
        let clips = [];
        try {
            const clipsJson = await jsx.callWithTimeout('getClipsInTrack', [trackIndex, 'video'], JSX_TIMEOUTS.DEFAULT);
            clips = typeof clipsJson === 'string' ? JSON.parse(clipsJson) : clipsJson;
            if (!Array.isArray(clips)) clips = [];
        } catch (e) {
            console.warn('[SPLICE Takes] Failed to get clips from track', trackIndex, ':', e.message);
            continue;
        }

        for (let clipIndex = 0; clipIndex < clips.length; clipIndex++) {
            const clip = clips[clipIndex];
            if (!clip) continue;

            const clipStart = clip.start;

            // Find which take this clip belongs to
            for (const take of takes) {
                const takeStart = take.start || take.startTime || 0;
                const takeEnd = take.end || take.endTime || 0;

                if (clipStart >= takeStart && clipStart < takeEnd) {
                    // Build label
                    const isBest = take.isBest || take.userMarkedBest;
                    const prefix = isBest ? '* ' : '';
                    const takeNum = take.takeNumber || takes.indexOf(take) + 1;
                    const desc = take.shortLabel || take.description || take.text || '';
                    const shortDesc = desc.split(' ').slice(0, 4).join(' ');
                    const label = `${prefix}Take ${takeNum} - ${shortDesc}`;

                    // Note: CEP doesn't have direct clip rename - using marker instead
                    // Create a marker at the clip start with the label
                    try {
                        await jsx.callWithTimeout('createMarker', [clipStart, label, 0, `Take ${takeNum}`, 5], JSX_TIMEOUTS.DEFAULT);
                        labeledCount++;
                    } catch (markerErr) {
                        console.warn('[SPLICE Takes] Failed to create marker at', clipStart, ':', markerErr.message);
                        failCount++;
                    }
                    break;
                }
            }
        }
    }

    if (failCount > 0) {
        console.warn(`[SPLICE Takes] Labeled ${labeledCount} clips, ${failCount} markers failed`);
    }

    return labeledCount;
}

/**
 * Apply best takes - disable non-best takes by removing clips
 * FIX: CEP-ERR-005 - Added comprehensive error handling for JSX calls
 * @returns {Promise<number>} Number of clips removed
 */
async function applyBestTakes() {
    const takes = getCurrentTakes();

    if (!takes || takes.length === 0) {
        throw new Error('No takes to apply');
    }

    // Check if sequence is open with timeout protection
    let seqResult;
    try {
        seqResult = await jsx.callWithTimeout('getActiveSequence', [], JSX_TIMEOUTS.QUICK);
        if (!seqResult || seqResult.error) {
            throw new Error('No project or sequence open');
        }
    } catch (e) {
        console.error('[SPLICE Takes] Failed to get active sequence:', e);
        if (e.code === 'JSX_TIMEOUT') {
            throw new Error('Premiere Pro is not responding. Try restarting the application.');
        }
        throw new Error('No project or sequence open: ' + e.message);
    }

    // Sort takes by start time (descending) - work from end to avoid time shifts
    const sortedTakes = [...takes].sort((a, b) => {
        const aStart = a.start || a.startTime || 0;
        const bStart = b.start || b.startTime || 0;
        return bStart - aStart;
    });

    let removedCount = 0;
    let failCount = 0;
    let numVideoTracks = 0;
    let numAudioTracks = 0;

    // Get track counts with error handling
    try {
        numVideoTracks = await jsx.callWithTimeout('getNumTracks', ['video'], JSX_TIMEOUTS.QUICK);
        numAudioTracks = await jsx.callWithTimeout('getNumTracks', ['audio'], JSX_TIMEOUTS.QUICK);
    } catch (e) {
        console.error('[SPLICE Takes] Failed to get track counts:', e);
        throw new Error('Could not read timeline tracks: ' + e.message);
    }

    for (const take of sortedTakes) {
        const isBest = take.isBest || take.userMarkedBest;
        if (isBest) {
            continue; // Keep best takes
        }

        const takeStart = take.start || take.startTime || 0;
        const takeEnd = take.end || take.endTime || 0;

        // Process video tracks
        for (let trackIndex = 0; trackIndex < numVideoTracks; trackIndex++) {
            try {
                const clipsJson = await jsx.callWithTimeout('getClipsInTrack', [trackIndex, 'video'], JSX_TIMEOUTS.DEFAULT);
                const clips = typeof clipsJson === 'string' ? JSON.parse(clipsJson) : clipsJson;
                if (!Array.isArray(clips)) continue;

                // Process from end to avoid index shifting
                for (let clipIndex = clips.length - 1; clipIndex >= 0; clipIndex--) {
                    const clip = clips[clipIndex];
                    if (!clip) continue;

                    // Check if clip is within the non-best take range
                    if (clip.start >= takeStart && clip.end <= takeEnd) {
                        try {
                            await jsx.callWithTimeout('removeClipByIndex', ['video', trackIndex, clipIndex], JSX_TIMEOUTS.DEFAULT);
                            removedCount++;
                        } catch (removeErr) {
                            console.warn('[SPLICE Takes] Failed to remove video clip:', removeErr.message);
                            failCount++;
                        }
                    }
                }
            } catch (e) {
                console.warn('[SPLICE Takes] Failed to process video track', trackIndex, ':', e.message);
            }
        }

        // Process audio tracks
        for (let trackIndex = 0; trackIndex < numAudioTracks; trackIndex++) {
            try {
                const clipsJson = await jsx.callWithTimeout('getClipsInTrack', [trackIndex, 'audio'], JSX_TIMEOUTS.DEFAULT);
                const clips = typeof clipsJson === 'string' ? JSON.parse(clipsJson) : clipsJson;
                if (!Array.isArray(clips)) continue;

                // Process from end to avoid index shifting
                for (let clipIndex = clips.length - 1; clipIndex >= 0; clipIndex--) {
                    const clip = clips[clipIndex];
                    if (!clip) continue;

                    // Check if clip is within the non-best take range
                    if (clip.start >= takeStart && clip.end <= takeEnd) {
                        try {
                            await jsx.callWithTimeout('removeClipByIndex', ['audio', trackIndex, clipIndex], JSX_TIMEOUTS.DEFAULT);
                            removedCount++;
                        } catch (removeErr) {
                            console.warn('[SPLICE Takes] Failed to remove audio clip:', removeErr.message);
                            failCount++;
                        }
                    }
                }
            } catch (e) {
                console.warn('[SPLICE Takes] Failed to process audio track', trackIndex, ':', e.message);
            }
        }
    }

    if (failCount > 0) {
        console.warn(`[SPLICE Takes] Removed ${removedCount} clips, ${failCount} removals failed`);
    }

    return removedCount;
}

/**
 * Add take markers to timeline
 * FIX: CEP-ERR-006 - Added error handling with partial success support
 * @returns {Promise<number>} Number of markers added
 */
async function addTakeMarkers() {
    const takes = getCurrentTakes();

    if (!takes || takes.length === 0) {
        throw new Error('No takes to mark');
    }

    let markerCount = 0;
    let failCount = 0;

    for (const take of takes) {
        try {
            const isBest = take.isBest || take.userMarkedBest;
            const takeStart = take.start || take.startTime || 0;
            const takeEnd = take.end || take.endTime || 0;
            const takeNum = take.takeNumber || takes.indexOf(take) + 1;
            const duration = takeEnd - takeStart;

            const name = `${isBest ? '* ' : ''}Take ${takeNum}`;
            const comments = take.shortLabel || take.description || '';
            const colorIndex = isBest ? 5 : 4; // Cerulean for best, Lavender for others

            await jsx.callWithTimeout('createMarker', [takeStart, name, duration, comments, colorIndex], JSX_TIMEOUTS.DEFAULT);
            markerCount++;
        } catch (e) {
            console.warn('[SPLICE Takes] Failed to add marker for take:', e.message);
            failCount++;
            // Continue adding other markers - partial success is acceptable
        }
    }

    if (failCount > 0) {
        console.warn(`[SPLICE Takes] Added ${markerCount} markers, ${failCount} failed`);
    }

    return markerCount;
}

// Legacy init functions - no longer needed
function initLabelTimeline() {
    // Now called directly via labelTakesOnTimeline()
}

function initApplyTakes() {
    // Now called directly via applyBestTakes()
}

// Export for window access
window.labelTakesOnTimeline = labelTakesOnTimeline;
window.applyBestTakes = applyBestTakes;
window.addTakeMarkers = addTakeMarkers;

