/**
 * Slice 8: Silence Detection Utilities (CEP Port)
 *
 * Provides core functionality for silence detection and removal.
 * Port of UXP slice8-silence.js for CEP compatibility.
 */

// Store detected silences
let currentSilences = [];

/**
 * Get current silences
 */
function getCurrentSilences() {
    return currentSilences;
}

/**
 * Set current silences (used by main.js)
 */
function setCurrentSilences(silences) {
    currentSilences = silences;
}

/**
 * Clear current silences
 */
function clearCurrentSilences() {
    currentSilences = [];
}

/**
 * Maps 0-100 sensitivity slider to detection parameters
 * Lower = keep more pauses, Higher = remove more aggressively
 */
function mapSensitivity(value) {
    const t = value / 100;
    return {
        dbThreshold: Math.round(-50 + (30 * t)),                    // -50dB to -20dB
        minDuration: parseFloat((2.0 - (1.7 * t)).toFixed(2)),      // 2.0s to 0.3s
        padding: parseFloat((0.2 - (0.15 * t)).toFixed(2))          // 0.2s to 0.05s
    };
}

/**
 * Get silence statistics
 * @returns {Object} Statistics about current silences
 */
function getSilenceStats() {
    if (!currentSilences || currentSilences.length === 0) {
        return {
            count: 0,
            totalDuration: 0,
            averageDuration: 0
        };
    }

    let totalDuration = 0;
    for (const silence of currentSilences) {
        totalDuration += (silence.end - silence.start);
    }

    return {
        count: currentSilences.length,
        totalDuration,
        averageDuration: totalDuration / currentSilences.length
    };
}

/**
 * Remove silences from timeline using razor and delete
 * Returns count of removed sections
 *
 * FIX: CEP-ERR-001 - Added comprehensive error handling for JSX calls
 * to prevent crashes on different Mac configurations
 */
async function removeSilencesFromTimeline() {
    if (!currentSilences || currentSilences.length === 0) {
        return 0;
    }

    // Check if sequence is open with timeout protection
    let seqResult;
    try {
        seqResult = await jsx.callWithTimeout('getActiveSequence', [], JSX_TIMEOUTS.QUICK);
        if (!seqResult || seqResult.error) {
            throw new Error('No project or sequence open');
        }
    } catch (e) {
        console.error('[SPLICE Silence] Failed to get active sequence:', e);
        if (e.code === 'JSX_TIMEOUT') {
            throw new Error('Premiere Pro is not responding. Try restarting the application.');
        }
        throw new Error('No project or sequence open: ' + e.message);
    }

    // Get sequence settings with error handling
    let settings;
    try {
        settings = await jsx.callWithTimeout('getSequenceSettings', [], JSX_TIMEOUTS.QUICK);
        if (!settings || !settings.frameRate) {
            throw new Error('Could not get sequence settings');
        }
    } catch (e) {
        console.error('[SPLICE Silence] Failed to get sequence settings:', e);
        throw new Error('Could not read sequence settings: ' + e.message);
    }

    // settings.frameRate is seconds per frame, so 1/frameRate = fps
    const frameRate = 1 / settings.frameRate; // frames per second

    // Sort silences by start time DESCENDING (work from end to avoid time shifts)
    const sortedSilences = [...currentSilences].sort((a, b) => b.start - a.start);

    let removedCount = 0;
    let numVideoTracks = 0;
    let numAudioTracks = 0;

    // Get track counts with error handling
    try {
        numVideoTracks = await jsx.callWithTimeout('getNumTracks', ['video'], JSX_TIMEOUTS.QUICK);
        numAudioTracks = await jsx.callWithTimeout('getNumTracks', ['audio'], JSX_TIMEOUTS.QUICK);
    } catch (e) {
        console.error('[SPLICE Silence] Failed to get track counts:', e);
        throw new Error('Could not read timeline tracks: ' + e.message);
    }

    for (const silence of sortedSilences) {
        const startSeconds = silence.start;
        const endSeconds = silence.end;

        // Convert to frames for razor operations
        const startFrames = Math.round(startSeconds * frameRate);
        const endFrames = Math.round(endSeconds * frameRate);

        // Razor at silence boundaries with error handling
        try {
            await jsx.callWithTimeout('razorSequenceAtSeconds', [startSeconds], JSX_TIMEOUTS.DEFAULT);
            await jsx.callWithTimeout('razorSequenceAtSeconds', [endSeconds], JSX_TIMEOUTS.DEFAULT);
        } catch (e) {
            console.warn('[SPLICE Silence] Razor operation failed at', startSeconds, '-', endSeconds, ':', e.message);
            // Continue with next silence - partial success is better than total failure
            continue;
        }

        // Delete clips that fall within silence regions
        // Process video tracks
        for (let trackIndex = 0; trackIndex < numVideoTracks; trackIndex++) {
            try {
                const clips = await jsx.callWithTimeout('getClipsInTrack', [trackIndex, 'video'], JSX_TIMEOUTS.DEFAULT);
                if (!clips || !Array.isArray(clips)) continue;

                // Process from end to avoid index shifting
                for (let clipIndex = clips.length - 1; clipIndex >= 0; clipIndex--) {
                    const clip = clips[clipIndex];
                    if (!clip) continue;

                    const clipCenter = (clip.start + clip.end) / 2;

                    // Check if clip center is within silence region
                    if (clipCenter > startSeconds && clipCenter < endSeconds) {
                        try {
                            await jsx.callWithTimeout('removeClipByIndex', ['video', trackIndex, clipIndex], JSX_TIMEOUTS.DEFAULT);
                            removedCount++;
                        } catch (removeErr) {
                            console.warn('[SPLICE Silence] Failed to remove video clip:', removeErr.message);
                        }
                    }
                }
            } catch (e) {
                console.warn('[SPLICE Silence] Failed to process video track', trackIndex, ':', e.message);
            }
        }

        // Process audio tracks
        for (let trackIndex = 0; trackIndex < numAudioTracks; trackIndex++) {
            try {
                const clips = await jsx.callWithTimeout('getClipsInTrack', [trackIndex, 'audio'], JSX_TIMEOUTS.DEFAULT);
                if (!clips || !Array.isArray(clips)) continue;

                // Process from end to avoid index shifting
                for (let clipIndex = clips.length - 1; clipIndex >= 0; clipIndex--) {
                    const clip = clips[clipIndex];
                    if (!clip) continue;

                    const clipCenter = (clip.start + clip.end) / 2;

                    // Check if clip center is within silence region
                    if (clipCenter > startSeconds && clipCenter < endSeconds) {
                        try {
                            await jsx.callWithTimeout('removeClipByIndex', ['audio', trackIndex, clipIndex], JSX_TIMEOUTS.DEFAULT);
                            removedCount++;
                        } catch (removeErr) {
                            console.warn('[SPLICE Silence] Failed to remove audio clip:', removeErr.message);
                        }
                    }
                }
            } catch (e) {
                console.warn('[SPLICE Silence] Failed to process audio track', trackIndex, ':', e.message);
            }
        }
    }

    return removedCount;
}

/**
 * Add silence markers to timeline without removing
 * FIX: CEP-ERR-002 - Added error handling with partial success support
 * @returns {Promise<number>} Number of markers added
 */
async function addSilenceMarkers() {
    if (!currentSilences || currentSilences.length === 0) {
        return 0;
    }

    let markerCount = 0;
    let failCount = 0;

    for (const silence of currentSilences) {
        try {
            const duration = silence.end - silence.start;
            const name = `Silence: ${duration.toFixed(1)}s`;
            const comments = `Start: ${formatTime(silence.start)} | End: ${formatTime(silence.end)}`;
            const colorIndex = 1; // Violet for silences

            await jsx.callWithTimeout('createMarker', [silence.start, name, duration, comments, colorIndex], JSX_TIMEOUTS.DEFAULT);
            markerCount++;
        } catch (e) {
            console.warn('[SPLICE Silence] Failed to add marker at', silence.start, ':', e.message);
            failCount++;
            // Continue adding other markers - partial success is acceptable
        }
    }

    if (failCount > 0) {
        console.warn(`[SPLICE Silence] Added ${markerCount} markers, ${failCount} failed`);
    }

    return markerCount;
}

/**
 * Delete all SPLICE silence markers from timeline
 * FIX: CEP-ERR-003 - Added error handling for marker deletion
 * @returns {Promise<number>} Number of markers deleted
 */
async function clearSilenceMarkers() {
    try {
        const result = await jsx.callWithTimeout('deleteMarkersByName', ['Silence:'], JSX_TIMEOUTS.DEFAULT);
        return result?.deleted || 0;
    } catch (e) {
        console.error('[SPLICE Silence] Failed to clear markers:', e.message);
        return 0;
    }
}

