  1071→function renderPreviewList() {
  1072→    if (!ui.previewList) return;
  1073→
  1074→    const html = [];
  1075→
  1076→    // Render silences
  1077→    previewSilences.forEach((silence, i) => {
  1078→        const isSelected = selectedSilenceIndices.has(i);
  1079→        const duration = silence.end - silence.start;
  1080→        html.push(`
  1081→            <div class="preview-item ${isSelected ? '' : 'excluded'}" data-index="${i}" data-type="silence">
  1082→                <input type="checkbox" class="preview-item-check" ${isSelected ? 'checked' : ''}>
  1083→                <div class="preview-item-info">
  1084→                    <div class="preview-item-time">${formatTime(silence.start)} - ${formatTime(silence.end)}</div>
  1085→                    <div class="preview-item-duration">${duration.toFixed(2)}s silence</div>
  1086→                </div>
  1087→            </div>
  1088→        `);
  1089→    });
  1090→
  1091→    // Render takes
  1092→    previewTakes.forEach((take, i) => {
  1093→        // SECURITY: Escape take label to prevent XSS
  1094→        const safeLabel = escapeHtml(take.label || 'Take ' + (take.takeNumber || i + 1));
  1095→        html.push(`
