  1624→/**
  1625→ * Look up license key by email address
  1626→ */
  1627→async function lookupLicense() {
  1628→    const lookupEmailInput = document.getElementById('lookupEmailInput');
  1629→    const licenseKeyInput = document.getElementById('licenseKeyInput');
  1630→
  1631→    if (!lookupEmailInput) return;
  1632→
  1633→    const email = lookupEmailInput.value.trim().toLowerCase();
  1634→    if (!email) {
  1635→        setStatus('Please enter your email address', true);
  1636→        return;
  1637→    }
  1638→
  1639→    // Basic email validation
  1640→    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  1641→    if (!emailRegex.test(email)) {
  1642→        setStatus('Please enter a valid email address', true);
  1643→        return;
  1644→    }
  1645→
  1646→    const lookupBtn = document.getElementById('lookupLicenseBtn');
