  1692→    const settings = getSettings();
  1693→    if (!settings.customerId) {
  1694→        ui.creditBadge.className = 'credit-badge login';
  1695→        ui.creditBadge.textContent = 'Login';
  1696→        ui.creditBadge.style.display = 'flex';
  1697→        return;
  1698→    }
  1699→
  1700→    try {
  1701→        const response = await fetchWithTimeout(
  1702→            `${getBackendUrl()}/credits`,
  1703→            {
  1704→                method: 'GET',
  1705→                headers: getAuthHeaders()
  1706→            }
  1707→        );
  1708→
  1709→        if (response.ok) {
  1710→            const data = await response.json();
  1711→            // Backend returns hoursRemaining (not remainingMinutes)
  1712→            const hours = (data.hoursRemaining || 0).toFixed(1);
  1713→            ui.creditBadge.textContent = `${hours}h`;
  1714→            // Low warning when less than 1 hour remaining
  1715→            ui.creditBadge.className = data.hoursRemaining < 1 ? 'credit-badge low' : 'credit-badge ok';
  1716→            ui.creditBadge.style.display = 'flex';
  1717→        } else {
  1718→            ui.creditBadge.className = 'credit-badge error';
  1719→            ui.creditBadge.textContent = 'Error';
  1720→            ui.creditBadge.style.display = 'flex';
  1721→        }
  1722→    } catch {
  1723→        ui.creditBadge.className = 'credit-badge error';
  1724→        ui.creditBadge.textContent = 'Offline';
  1725→        ui.creditBadge.style.display = 'flex';
  1726→    }
  1727→}
  1728→
  1729→// ============================================================================
  1730→// HELP BUTTON
  1731→// ============================================================================
  1732→function initHelpButton() {
  1733→    const helpBtn = document.getElementById('helpBtn');
  1734→    if (helpBtn) {
  1735→        helpBtn.addEventListener('click', () => {
  1736→            setStatus('Silence: removes quiet gaps | Takes: detects repeated content | Chapters: AI topic segmentation');
  1737→        });
  1738→    }
  1739→}
