    26→    BROWN: 15,
    27→    YELLOW: 16
    28→};
    29→
    30→const SEGMENT_COLORS = {
    31→    speech: SPLICE_COLORS.FOREST,
    32→    take: SPLICE_COLORS.LAVENDER,
    33→    best_take: SPLICE_COLORS.CERULEAN,
