   114→            const data = await response.json();
   115→            animatedCaptionsState.templates = data.templates || [];
   116→            console.log(`[SPLICE Captions] Loaded ${animatedCaptionsState.templates.length} templates`);
   117→        }
   118→    } catch (err) {
   119→        console.error('[SPLICE Captions] Failed to load templates:', err);
   120→        // Use fallback templates
   121→        animatedCaptionsState.templates = [
   122→            { id: 'mrbeast', name: 'MrBeast', description: 'Bold, uppercase, colorful' },
   123→            { id: 'hormozi', name: 'Hormozi', description: 'Clean, yellow highlight' },
   124→            { id: 'gaming', name: 'Gaming', description: 'Neon glow, glitch effects' },
   125→            { id: 'corporate', name: 'Corporate', description: 'Subtle, professional' },
   126→            { id: 'karaoke', name: 'Karaoke', description: 'Word-by-word highlight' }
   127→        ];
   128→    }
   129→}
   130→
   131→// ============================================================================
   132→// EVENT LISTENERS
   133→// ============================================================================
   134→function setupCaptionEventListeners() {
   135→    // Template gallery delegation
   136→    if (captionsUI.templateGallery) {
   137→        captionsUI.templateGallery.addEventListener('click', (e) => {
   138→            const card = e.target.closest('.caption-template-card');
   139→            if (card) {
   140→                selectCaptionTemplate(card.dataset.templateId);
   141→            }
   142→        });
   143→    }
   144→
   145→    // Generate button
   146→    if (captionsUI.generateBtn) {
   147→        captionsUI.generateBtn.addEventListener('click', generateCaptions);
   148→    }
   149→
   150→    // Export buttons
   151→    if (captionsUI.exportSrtBtn) {
   152→        captionsUI.exportSrtBtn.addEventListener('click', () => exportCaptions('srt'));
   153→    }
   154→    if (captionsUI.exportMogrtBtn) {
   155→        captionsUI.exportMogrtBtn.addEventListener('click', () => exportCaptions('mogrt'));
   156→    }
   157→
   158→    // Apply to timeline button
   159→    if (captionsUI.applyBtn) {
   160→        captionsUI.applyBtn.addEventListener('click', applyCaptionsToTimeline);
   161→    }
   162→
   163→    // Copy button
   164→    if (captionsUI.copyBtn) {
   165→        captionsUI.copyBtn.addEventListener('click', copyCaptionsToClipboard);
   166→    }
   167→
   168→    // Settings changes
   169→    if (captionsUI.wordsPerLine) {
   170→        captionsUI.wordsPerLine.addEventListener('change', (e) => {
   171→            animatedCaptionsState.settings.maxWordsPerLine = parseInt(e.target.value) || 4;
   172→        });
   173→    }
   174→    if (captionsUI.highlightKeywords) {
   175→        captionsUI.highlightKeywords.addEventListener('change', (e) => {
   176→            animatedCaptionsState.settings.highlightKeywords = e.target.checked;
   177→        });
   178→    }
   179→    if (captionsUI.insertEmojis) {
   180→        captionsUI.insertEmojis.addEventListener('change', (e) => {
   181→            animatedCaptionsState.settings.insertEmojis = e.target.checked;
   182→            // Toggle emoji frequency group visibility
   183→            const emojiFrequencyGroup = document.getElementById('emojiFrequencyGroup');
   184→            if (emojiFrequencyGroup) {
   185→                emojiFrequencyGroup.style.display = e.target.checked ? 'block' : 'none';
   186→            }
   187→        });
   188→    }
   189→    if (captionsUI.emojiFrequency) {
   190→        captionsUI.emojiFrequency.addEventListener('change', (e) => {
   191→            animatedCaptionsState.settings.emojiFrequency = e.target.value;
   192→        });
   193→    }
   194→}
   195→
   196→// ============================================================================
   197→// TEMPLATE GALLERY
   198→// ============================================================================
   199→function renderTemplateGallery() {
   200→    if (!captionsUI.templateGallery) return;
   201→
   202→    const fragment = document.createDocumentFragment();
   203→
   204→    animatedCaptionsState.templates.forEach(template => {
   205→        const card = document.createElement('div');
   206→        card.className = `caption-template-card ${animatedCaptionsState.selectedTemplate === template.id ? 'selected' : ''}`;
   207→        card.dataset.templateId = template.id;
   208→        card.setAttribute('role', 'button');
   209→        card.setAttribute('tabindex', '0');
   210→        card.setAttribute('aria-label', `Select ${escapeHtmlCaptions(template.name)} template`);
   211→
   212→        // SECURITY: Escape template data to prevent XSS (even though it comes from backend/fallback)
   213→        const safeId = escapeHtmlCaptions(template.id);
   214→        const safeName = escapeHtmlCaptions(template.name);
   215→        const safeDesc = escapeHtmlCaptions(template.description);
   216→
   217→        card.innerHTML = `
   218→            <div class="template-preview template-preview-${safeId}">
   219→                <span class="template-preview-text">Hello World</span>
   220→            </div>
   221→            <div class="template-info">
   222→                <span class="template-name">${safeName}</span>
   223→                <span class="template-desc">${safeDesc}</span>
   224→            </div>
   225→        `;
   226→
   227→        fragment.appendChild(card);
   228→    });
   229→
   230→    captionsUI.templateGallery.innerHTML = '';
   231→    captionsUI.templateGallery.appendChild(fragment);
   232→}
   233→
   234→function selectCaptionTemplate(templateId) {
   235→    animatedCaptionsState.selectedTemplate = templateId;
   236→
   237→    // Update UI
   238→    document.querySelectorAll('.caption-template-card').forEach(card => {
   239→        card.classList.toggle('selected', card.dataset.templateId === templateId);
   240→    });
   241→
   242→    console.log(`[SPLICE Captions] Selected template: ${templateId}`);
   243→
   244→    // Re-apply template if we have captions
   245→    if (animatedCaptionsState.captions) {
   246→        applyTemplateToExisting(templateId);
   247→    }
   248→}
   249→
   250→async function applyTemplateToExisting(templateId) {
   251→    if (!animatedCaptionsState.captions) return;
   252→
   253→    try {
   254→        const apiUrl = getBackendUrl();
   255→        const response = await fetchWithTimeout(`${apiUrl}/captions/apply-template`, {
   256→            method: 'POST',
   257→            headers: getAuthHeaders(),
   258→            body: JSON.stringify({
   259→                captionData: { captions: animatedCaptionsState.captions },
   260→                templateId
   261→            })
   262→        }, 30000);
   263→
   264→        if (response.ok) {
   265→            const data = await response.json();
   266→            animatedCaptionsState.captions = data.captions;
   267→            renderCaptionPreview(true);  // Immediate after template apply
   268→        }
   269→    } catch (err) {
   270→        console.error('[SPLICE Captions] Failed to apply template:', err);
   271→    }
   272→}
   273→
   274→// ============================================================================
   275→// CAPTION GENERATION
   276→// ============================================================================
   277→async function generateCaptions() {
   278→    const transcript = getLastTranscript();
   279→    if (!transcript) {
   280→        showCaptionStatus('No transcript available. Run transcription first.', 'error');
   281→        return;
   282→    }
   283→
   284→    // Check online status
   285→    if (typeof isOnline === 'function' && !isOnline()) {
   286→        showCaptionStatus('Offline - Check your connection', 'error');
   287→        return;
   288→    }
   289→
   290→    animatedCaptionsState.isGenerating = true;
   291→    updateGenerateButton(true);
   292→    showCaptionStatus('Generating animated captions...', 'info');
   293→
   294→    try {
   295→        const apiUrl = getBackendUrl();
   296→        const response = await fetchWithTimeout(`${apiUrl}/captions/animate`, {
   297→            method: 'POST',
   298→            headers: getAuthHeaders(),
   299→            body: JSON.stringify({
   300→                transcript,
   301→                template: animatedCaptionsState.selectedTemplate,
   302→                settings: animatedCaptionsState.settings
   303→            })
   304→        }, 120000);
   305→
   306→        if (!response.ok) {
   307→            const errorMsg = await parseErrorResponse(response);
   308→            throw new Error(errorMsg);
   309→        }
   310→
   311→        const data = await response.json();
   312→        animatedCaptionsState.captions = data.captions;
   313→        animatedCaptionsState.keywords = data.keywords || [];
   314→
   315→        // Update UI
   316→        renderCaptionPreview(true);  // Immediate after generation
   317→        updateCaptionStats(data);
   318→        showCaptionExportActions(true);
   319→        showCaptionStatus(`Generated ${data.totalCaptions} caption lines`, 'success');
   320→
   321→    } catch (err) {
   322→        console.error('[SPLICE Captions] Generation error:', err);
   323→        showCaptionStatus(err.message, 'error');
   324→    } finally {
   325→        animatedCaptionsState.isGenerating = false;
   326→        updateGenerateButton(false);
   327→    }
   328→}
   329→
   330→function getLastTranscript() {
   331→    // Try main module state
   332→    if (window.spliceState?.lastTranscript) {
   333→        return window.spliceState.lastTranscript;
   334→    }
   335→    // Try local state
   336→    if (animatedCaptionsState.lastTranscript) {
   337→        return animatedCaptionsState.lastTranscript;
   338→    }
   339→    return null;
   340→}
   341→
   342→function setCaptionTranscript(transcript) {
   343→    animatedCaptionsState.lastTranscript = transcript;
   344→}
   345→
   346→// ============================================================================
   347→// CAPTION PREVIEW
   348→// ============================================================================
   349→
   350→/**
   351→ * FIX: CEP-CRIT-009 - Animated Captions Preview Debounce
   352→ * Debounced version prevents layout thrashing from rapid calls
   353→ */
   354→function renderCaptionPreview(immediate = false) {
   355→    // Clear existing timer
   356→    if (animatedCaptionsState.previewDebounceTimer) {
   357→        clearTimeout(animatedCaptionsState.previewDebounceTimer);
   358→    }
   359→
   360→    // If immediate, render now
   361→    if (immediate) {
   362→        renderCaptionPreviewImmediate();
   363→        return;
   364→    }
   365→
   366→    // Otherwise, debounce with 150ms delay
   367→    animatedCaptionsState.previewDebounceTimer = setTimeout(() => {
   368→        renderCaptionPreviewImmediate();
   369→    }, 150);
   370→}
   371→
   372→function renderCaptionPreviewImmediate() {
   373→    if (!captionsUI.previewContainer || !animatedCaptionsState.captions) return;
   374→
   375→    const captions = animatedCaptionsState.captions.slice(0, 10);
   376→    const fragment = document.createDocumentFragment();
   377→
   378→    const list = document.createElement('div');
   379→    list.className = 'caption-preview-list';
   380→
   381→    captions.forEach((caption, i) => {
   382→        const item = document.createElement('div');
   383→        item.className = 'caption-preview-item';
   384→        item.dataset.index = i;
   385→
   386→        const hasKeywords = caption.words?.some(w => w.isKeyword);
   387→        item.innerHTML = `
   388→            <span class="caption-time">${formatCaptionTime(caption.start)}</span>
   389→            <span class="caption-text ${hasKeywords ? 'has-keywords' : ''}">${formatCaptionText(caption)}</span>
   390→        `;
   391→
   392→        list.appendChild(item);
   393→    });
   394→
   395→    if (animatedCaptionsState.captions.length > 10) {
   396→        const more = document.createElement('div');
   397→        more.className = 'caption-preview-more';
   398→        more.textContent = `+ ${animatedCaptionsState.captions.length - 10} more captions`;
   399→        list.appendChild(more);
   400→    }
   401→
   402→    fragment.appendChild(list);
   403→
   404→    captionsUI.previewContainer.innerHTML = '';
   405→    captionsUI.previewContainer.appendChild(fragment);
   406→}
   407→
   408→// SECURITY: Escape HTML to prevent XSS
   409→function escapeHtmlCaptions(str) {
   410→    if (!str) return '';
   411→    const div = document.createElement('div');
   412→    div.textContent = str;
   413→    return div.innerHTML;
   414→}
   415→
   416→function formatCaptionText(caption) {
   417→    if (!caption.words) return escapeHtmlCaptions(caption.text);
   418→
   419→    // SECURITY: Escape user-provided word content to prevent XSS
   420→    return caption.words.map(word => {
   421→        const safeWord = escapeHtmlCaptions(word.word);
   422→        if (word.isKeyword) {
   423→            return `<span class="caption-keyword">${safeWord}</span>`;
   424→        }
   425→        if (word.emoji) {
   426→            const safeEmoji = escapeHtmlCaptions(word.emoji);
   427→            return `${safeWord} ${safeEmoji}`;
   428→        }
   429→        return safeWord;
   430→    }).join(' ');
   431→}
   432→
   433→function formatCaptionTime(seconds) {
   434→    const mins = Math.floor(seconds / 60);
   435→    const secs = Math.floor(seconds % 60);
   436→    return `${mins}:${secs.toString().padStart(2, '0')}`;
   437→}
   438→
   439→function updateCaptionStats(data) {
   440→    if (!captionsUI.stats) return;
   441→
   442→    captionsUI.stats.innerHTML = `
   443→        <span class="stat-item">${data.totalCaptions} lines</span>
   444→        <span class="stat-item">${data.totalWords} words</span>
   445→        <span class="stat-item">${data.keywords?.length || 0} keywords</span>
   446→        <span class="stat-item">${formatCaptionTime(data.duration)}</span>
   447→    `;
   448→}
   449→
   450→// ============================================================================
   451→// EXPORT FUNCTIONS
   452→// ============================================================================
   453→async function exportCaptions(format) {
   454→    if (!animatedCaptionsState.captions) {
   455→        showCaptionStatus('No captions to export. Generate captions first.', 'error');
   456→        return;
   457→    }
   458→
   459→    showCaptionStatus(`Exporting ${format.toUpperCase()}...`, 'info');
   460→
   461→    try {
   462→        const apiUrl = getBackendUrl();
   463→        let endpoint, body;
   464→
   465→        if (format === 'mogrt') {
   466→            endpoint = '/captions/export/mogrt';
   467→            body = {
   468→                captions: animatedCaptionsState.captions,
   469→                settings: { frameRate: 30, width: 1920, height: 1080 }
   470→            };
   471→        } else {
   472→            endpoint = '/export/captions';
   473→            body = {
   474→                transcript: { words: flattenCaptionWords() },
   475→                format
   476→            };
   477→        }
   478→
   479→        const response = await fetchWithTimeout(`${apiUrl}${endpoint}`, {
   480→            method: 'POST',
   481→            headers: getAuthHeaders(),
   482→            body: JSON.stringify(body)
   483→        }, 60000);
   484→
   485→        if (!response.ok) {
   486→            const errorMsg = await parseErrorResponse(response);
   487→            throw new Error(errorMsg);
   488→        }
   489→
   490→        const data = await response.json();
   491→
   492→        if (format === 'mogrt') {
   493→            downloadJSON(data.data, 'animated-captions-mogrt.json');
   494→            showCaptionStatus('MOGRT data exported', 'success');
   495→        } else {
   496→            downloadText(data.content, `captions.${format}`);
   497→            showCaptionStatus(`${format.toUpperCase()} exported`, 'success');
   498→        }
   499→
   500→    } catch (err) {
   501→        console.error('[SPLICE Captions] Export error:', err);
   502→        showCaptionStatus(err.message, 'error');
   503→    }
   504→}
   505→
   506→function flattenCaptionWords() {
   507→    if (!animatedCaptionsState.captions) return [];
   508→
   509→    const words = [];
   510→    animatedCaptionsState.captions.forEach(caption => {
   511→        if (caption.words) {
   512→            caption.words.forEach(w => words.push(w));
   513→        }
   514→    });
   515→    return words;
   516→}
   517→
   518→async function copyCaptionsToClipboard() {
   519→    if (!animatedCaptionsState.captions) {
   520→        showCaptionStatus('No captions to copy', 'error');
   521→        return;
   522→    }
   523→
   524→    const text = animatedCaptionsState.captions
   525→        .map(c => `[${formatCaptionTime(c.start)}] ${c.text}`)
   526→        .join('\n');
   527→
   528→    try {
   529→        await navigator.clipboard.writeText(text);
   530→        showCaptionStatus('Captions copied to clipboard', 'success');
   531→    } catch (err) {
   532→        console.error('[SPLICE Captions] Copy failed:', err);
   533→        showCaptionStatus('Failed to copy', 'error');
   534→    }
   535→}
   536→
   537→// ============================================================================
   538→// TIMELINE INTEGRATION
   539→// ============================================================================
   540→async function applyCaptionsToTimeline() {
   541→    if (!animatedCaptionsState.captions) {
   542→        showCaptionStatus('No captions to apply', 'error');
   543→        return;
   544→    }
   545→
   546→    // FIX: CEP-CRIT-008 - Animated Captions Timeline Loop
   547→    // Batch process markers (10 at a time) to prevent 15s UI freeze
   548→    const BATCH_SIZE = 10;
   549→    const totalCaptions = animatedCaptionsState.captions.length;
   550→    let processed = 0;
   551→
   552→    showCaptionStatus(`Applying ${totalCaptions} caption markers...`, 'info');
   553→
   554→    try {
   555→        // Process in batches
   556→        for (let i = 0; i < totalCaptions; i += BATCH_SIZE) {
   557→            const batch = animatedCaptionsState.captions.slice(i, i + BATCH_SIZE);
   558→
   559→            // Process current batch
   560→            await Promise.all(batch.map(caption => {
   561→                const duration = caption.end - caption.start;
   562→                return jsx.call('createMarker', caption.start, caption.text, duration, null, 5); // CERULEAN color
   563→            }));
   564→
   565→            processed += batch.length;
   566→
   567→            // Update progress
   568→            const percent = Math.round((processed / totalCaptions) * 100);
   569→            showCaptionStatus(`Applying caption markers... ${processed}/${totalCaptions} (${percent}%)`, 'info');
   570→
   571→            // Brief pause to prevent UI blocking
   572→            if (processed < totalCaptions) {
   573→                await new Promise(resolve => setTimeout(resolve, 100));
   574→            }
   575→        }
   576→
   577→        showCaptionStatus(`Added ${totalCaptions} caption markers`, 'success');
   578→    } catch (err) {
   579→        console.error('[SPLICE Captions] Apply error:', err);
   580→        showCaptionStatus(err.message, 'error');
   581→    }
   582→}
   583→
   584→// ============================================================================
   585→// UI HELPERS
   586→// ============================================================================
   587→function showCaptionExportActions(show) {
   588→    const exportActions = document.getElementById('captionExportActions');
   589→    if (exportActions) {
   590→        exportActions.classList.toggle('hidden', !show);
   591→    }
   592→}
   593→
   594→function showCaptionStatus(message, type = 'info') {
   595→    if (!captionsUI.status) return;
   596→
   597→    captionsUI.status.textContent = message;
   598→    captionsUI.status.className = `caption-status caption-status-${type}`;
   599→
   600→    // Auto-clear after 5 seconds for success/info
   601→    if (type !== 'error') {
   602→        setTimeout(() => {
   603→            captionsUI.status.textContent = '';
   604→            captionsUI.status.className = 'caption-status';
   605→        }, 5000);
   606→    }
   607→}
   608→
   609→function updateGenerateButton(isGenerating) {
   610→    if (!captionsUI.generateBtn) return;
   611→
   612→    captionsUI.generateBtn.disabled = isGenerating;
   613→    captionsUI.generateBtn.textContent = isGenerating ? 'Generating...' : 'Generate Captions';
   614→}
   615→
   616→function downloadJSON(data, filename) {
   617→    const blob = new Blob([JSON.stringify(data, null, 2)], { type: 'application/json' });
   618→    const url = URL.createObjectURL(blob);
   619→    const a = document.createElement('a');
   620→    a.href = url;
   621→    a.download = filename;
   622→    a.click();
   623→    URL.revokeObjectURL(url);
   624→}
   625→
   626→function downloadText(content, filename) {
   627→    const blob = new Blob([content], { type: 'text/plain' });
   628→    const url = URL.createObjectURL(blob);
   629→    const a = document.createElement('a');
   630→    a.href = url;
   631→    a.download = filename;
   632→    a.click();
   633→    URL.revokeObjectURL(url);
   634→}
   635→
   636→// ============================================================================
   637→// EXPORTS
   638→// ============================================================================
   639→if (typeof window !== 'undefined') {
   640→    window.initAnimatedCaptions = initAnimatedCaptions;
   641→    window.spliceAnimatedCaptions = {
   642→        init: initAnimatedCaptions,
   643→        generate: generateCaptions,
   644→        setTranscript: setCaptionTranscript,
   645→        selectTemplate: selectCaptionTemplate,
   646→        export: exportCaptions,
   647→        applyToTimeline: applyCaptionsToTimeline,
   648→        getState: () => animatedCaptionsState
   649→    };
   650→}
   651→

