   311→// ============================================================================
   312→// ANALYSIS
   313→// ============================================================================
   314→
   315→/**
   316→ * Analyze current video for reframe
   317→ */
   318→async function analyzeForReframe() {
   319→  // Check tier access - Social Reframe requires Pro or Team
   320→  if (typeof hasFeatureAccess === 'function' && !hasFeatureAccess('social_reframe')) {
   321→    const requiredTier = typeof getRequiredTier === 'function' ? getRequiredTier('social_reframe') : 'Pro';
   322→    showReframeStatus(`Social Reframe requires ${requiredTier} tier or higher`, 'error');
   323→    if (typeof showUpgradeModal === 'function') {
   324→      showUpgradeModal('Social Reframe', requiredTier);
   325→    }
   326→    return;
   327→  }
   328→
   329→  const videoPath = getCurrentVideoPath();
   330→  if (!videoPath) {
   331→    showReframeStatus('No video selected', 'error');
   332→    return;
   333→  }
   334→
   335→  socialReframeState.isAnalyzing = true;
   336→  updateAnalyzeButton(true);
   337→  showReframeStatus('Analyzing video for face tracking...', 'info');
   338→
   339→  try {
   340→    const backendUrl = typeof getBackendUrl === 'function' ? getBackendUrl() : 'https://127.0.0.1:3847';
   341→    const headers = typeof getAuthHeaders === 'function' ? getAuthHeaders() : { 'Content-Type': 'application/json' };
   342→    const fetchFn = typeof fetchWithTimeout === 'function' ? fetchWithTimeout : fetch;
   343→
   344→    const slider = reframeElements.smoothingSlider || document.getElementById('motion-smoothing-slider');
   345→    const smoothing = slider?.value || 0.3;
   346→
   347→    // FIX: CEP-CRIT-015 - Social Reframe Timeouts (90s for analysis)
   348→    const response = await fetchFn(`${backendUrl}/reframe/analyze`, {
   349→      method: 'POST',
   350→      headers,
   351→      body: JSON.stringify({
   352→        videoPath,
   353→        options: {
   354→          sampleRate: 0.5,
   355→          smoothing: parseFloat(smoothing)
   356→        }
   357→      })
   358→    }, 90000);
   359→
   360→    if (!response.ok) {
   361→      const err = await response.json();
   362→      throw new Error(err.error || 'Analysis failed');
   363→    }
   364→
   365→    const data = await response.json();
   366→    socialReframeState.analysis = data;
   367→    socialReframeState.currentVideoPath = videoPath;
   368→
   369→    // Update UI
   370→    renderAnalysisResults(data);
   371→    updateCropPreview();
   372→    showReframeStatus(`Found ${data.faceTracking.tracksFound} face(s)`, 'success');
   373→
   374→  } catch (err) {
   375→    console.error('[SPLICE] Reframe analysis error:', err);
   376→    showReframeStatus(err.message, 'error');
   377→  } finally {
   378→    socialReframeState.isAnalyzing = false;
   379→    updateAnalyzeButton(false);
   380→  }
   381→}
   382→
   383→/**
   384→ * Get current video path from main module or JSX
   385→ */
   386→function getCurrentVideoPath() {
   387→  // Try to get from main module state
   388→  if (window.spliceState?.currentVideoPath) {
   389→    return window.spliceState.currentVideoPath;
   390→  }
   391→
   392→  // Try from active sequence
   393→  if (window.spliceState?.activeSequence?.projectItem?.treePath) {
   394→    return window.spliceState.activeSequence.projectItem.treePath;
   395→  }
   396→
   397→  return socialReframeState.currentVideoPath;
   398→}
   399→
   400→/**
   401→ * Set video path for reframe
   402→ */
   403→function setVideoPath(videoPath) {
   404→  socialReframeState.currentVideoPath = videoPath;
   405→}
   406→
   407→/**
   408→ * Render analysis results
   409→ */
   410→function renderAnalysisResults(analysis) {
   411→  const container = reframeElements.analysisResults || document.getElementById('reframe-analysis-results');
   412→  if (!container) return;
   413→
   414→  // SECURITY: Escape analysis data to prevent XSS
   415→  const html = `
   416→    <div class="analysis-summary">
   417→      <div class="analysis-stat">
   418→        <span class="stat-label">Faces Found</span>
   419→        <span class="stat-value">${escapeHtmlReframe(String(analysis.faceTracking.tracksFound))}</span>
   420→      </div>
   421→      <div class="analysis-stat">
   422→        <span class="stat-label">Video Duration</span>
   423→        <span class="stat-value">${escapeHtmlReframe(formatDuration(analysis.videoInfo.duration))}</span>
   424→      </div>
   425→      <div class="analysis-stat">
   426→        <span class="stat-label">Resolution</span>
   427→        <span class="stat-value">${escapeHtmlReframe(String(analysis.videoInfo.width))}x${escapeHtmlReframe(String(analysis.videoInfo.height))}</span>
   428→      </div>
   429→    </div>
   430→    <div class="analysis-formats">
   431→      ${Object.entries(analysis.suggestions).map(([key, suggestion]) => `
   432→        <div class="format-suggestion" data-format="${escapeHtmlReframe(key)}">
   433→          <span class="format-name">${escapeHtmlReframe(getAspectRatioName(key))}</span>
   434→          <span class="format-method">${escapeHtmlReframe(suggestion.method)}</span>
   435→          <span class="format-keyframes">${escapeHtmlReframe(String(suggestion.totalKeyframes || 0))} keyframes</span>
   436→        </div>
   437→      `).join('')}
   438→    </div>
   439→  `;
   440→
   441→  container.innerHTML = html;
   442→}
   443→
   444→/**
   445→ * Get aspect ratio name
   446→ */
   447→function getAspectRatioName(id) {
   448→  const aspect = socialReframeState.aspectRatios.find(a => a.id === id);
   449→  return aspect?.name || id;
   450→}
   451→
   452→// ============================================================================
   453→// PREVIEW
   454→// ============================================================================
   455→
   456→/**
   457→ * Update crop preview canvas
   458→ */
   459→function updateCropPreview() {
   460→  const canvas = reframeElements.previewCanvas || document.getElementById('reframe-preview-canvas');
   461→  if (!canvas || !socialReframeState.analysis) return;
   462→
   463→  const ctx = canvas.getContext('2d');
   464→  const { videoInfo, suggestions } = socialReframeState.analysis;
   465→  const aspectId = socialReframeState.selectedAspectRatio;
   466→  const suggestion = suggestions[aspectId];
   467→
   468→  if (!suggestion) return;
   469→
   470→  // Clear canvas
   471→  ctx.clearRect(0, 0, canvas.width, canvas.height);
   472→
   473→  // Draw video frame representation
   474→  const scale = Math.min(canvas.width / videoInfo.width, canvas.height / videoInfo.height);
   475→  const videoW = videoInfo.width * scale;
   476→  const videoH = videoInfo.height * scale;
   477→  const videoX = (canvas.width - videoW) / 2;
   478→  const videoY = (canvas.height - videoH) / 2;
   479→
   480→  // Draw video background
   481→  ctx.fillStyle = '#333';
   482→  ctx.fillRect(videoX, videoY, videoW, videoH);
   483→
   484→  // Draw crop region
   485→  const crop = suggestion.static ? suggestion.crop : suggestion.keyframes[0]?.crop;
   486→  if (crop) {
   487→    ctx.strokeStyle = '#00ff00';
   488→    ctx.lineWidth = 2;
   489→    ctx.strokeRect(
   490→      videoX + crop.x * videoW,
   491→      videoY + crop.y * videoH,
   492→      crop.width * videoW,
   493→      crop.height * videoH
   494→    );
   495→
   496→    // Draw face indicator
   497→    if (socialReframeState.analysis.faceTracking.tracks[0]) {
   498→      const face = socialReframeState.analysis.faceTracking.tracks[0].positions[0];
   499→      if (face) {
   500→        ctx.fillStyle = 'rgba(255, 0, 0, 0.3)';
   501→        ctx.fillRect(
   502→          videoX + face.x * videoW,
   503→          videoY + face.y * videoH,
   504→          face.width * videoW,
   505→          face.height * videoH
   506→        );
   507→      }
   508→    }
   509→  }
   510→
   511→  // Draw safe zones if enabled
   512→  if (socialReframeState.showSafeZones && socialReframeState.safeZones) {
   513→    renderSafeZoneOnCanvas(ctx, videoX, videoY, videoW, videoH);
   514→  }
   515→}
   516→
   517→/**
   518→ * Render safe zone overlay on canvas
   519→ */
   520→function renderSafeZoneOnCanvas(ctx, x, y, width, height) {
   521→  const sz = socialReframeState.safeZones.safeZone;
   522→  if (!sz) return;
   523→
   524→  ctx.fillStyle = 'rgba(255, 0, 0, 0.15)';
   525→
   526→  // Top safe zone
   527→  ctx.fillRect(x, y, width, height * sz.top);
   528→
   529→  // Bottom safe zone
   530→  ctx.fillRect(x, y + height * (1 - sz.bottom), width, height * sz.bottom);
   531→
   532→  // Left safe zone
   533→  ctx.fillRect(x, y, width * sz.left, height);
   534→
   535→  // Right safe zone
   536→  ctx.fillRect(x + width * (1 - sz.right), y, width * sz.right, height);
   537→}
   538→
   539→/**
   540→ * Render safe zone overlay
   541→ */
   542→function renderSafeZoneOverlay() {
   543→  updateCropPreview();
   544→}
   545→
   546→/**
   547→ * Toggle safe zones visibility
   548→ */
   549→function toggleSafeZones() {
   550→  socialReframeState.showSafeZones = !socialReframeState.showSafeZones;
   551→  updateCropPreview();
   552→
   553→  const btn = reframeElements.toggleSafeZonesBtn || document.getElementById('toggle-safe-zones-btn');
   554→  if (btn) {
   555→    btn.classList.toggle('active', socialReframeState.showSafeZones);
   556→  }
   557→}
